(97, 'সহীহ আল বুখারী - ৫ম খন্ড', '', 59, 0, 'সহীহ বুখারী (আরবি: صحيح البخاري‎‎) একটি প্রসিদ্ধ হাদীস বিষয়ক গ্রন্থ। এর আসল নাম, الجامع المسند الصحيح المختصر من أمور رسول اللّٰه صلى اللّٰه عليه و سلم وسننه وأيامه আহলুস সুন্নাহ ওয়াল জামায়াতের মতে, এটি কুতুব আস-সিত্তাহ অর্থাৎ হাদীস বিষয়ক প্রধান ছয়টি গ্রন্থের অন্যতম। ইমাম বুখারি মুহাম্মাদের বাণীসংবলিত এই গ্রন্থটি সংকলন করেছেন। এই গ্রন্থটিকে ইসলামী বিধি-বিধান বিষয়ে কোরআনের পরে সবচাইতে নির্ভরযোগ্য প্রামাণ্য গ্রন্থ হিসেবে বিবেচনা করা হয়।', '6', 'http://xeroxtree.com/pdf/sahih_al_bukhari_05.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201605/980_1.JPG', 'আধুনিক প্রকাশনী', '16.99', '0', 0, 0, NULL, NULL),
(98, 'সহীহ আল বুখারী - ৬ষ্ঠ খন্ড', '', 59, 0, 'সহীহ বুখারী (আরবি: صحيح البخاري‎‎) একটি প্রসিদ্ধ হাদীস বিষয়ক গ্রন্থ। এর আসল নাম, الجامع المسند الصحيح المختصر من أمور رسول اللّٰه صلى اللّٰه عليه و سلم وسننه وأيامه আহলুস সুন্নাহ ওয়াল জামায়াতের মতে, এটি কুতুব আস-সিত্তাহ অর্থাৎ হাদীস বিষয়ক প্রধান ছয়টি গ্রন্থের অন্যতম। ইমাম বুখারি মুহাম্মাদের বাণীসংবলিত এই গ্রন্থটি সংকলন করেছেন। এই গ্রন্থটিকে ইসলামী বিধি-বিধান বিষয়ে কোরআনের পরে সবচাইতে নির্ভরযোগ্য প্রামাণ্য গ্রন্থ হিসেবে বিবেচনা করা হয়।', '6', 'http://xeroxtree.com/pdf/sahih_al_bukhari_06.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201605/981_1.JPG', 'আধুনিক প্রকাশনী', '15.04', '0', 0, 0, NULL, NULL),
(100, 'সহীহ মুসলিম - ১ম খন্ড', '', 60, 0, 'সহীহ মুসলিম  হাদিসের অন্যতম একটি গ্রন্থের নাম। ইমাম মুসলিম বিন হাজ্জাজ ইবনে মুসলিম আল কুশাইরী হলেন এই মহান গ্রন্থের সংকলক। বর্তমান ইরানের বৃহত্তর খোরাসান প্রদেশের নিশাপুরে ২০৪ হিজরিতে তিনি জন্মগ্রহণ করেন। মৃত্যুবরণ করেন ২৬১ হিজরির ২৬ শে রজব এবং নিশাপুরেই তাকে দাফন করা হয়।\r\n', '6', 'http://xeroxtree.com/pdf/muslim_01.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/710_1.jpg', 'বাংলাদেশ ইসলামিক সেন্টার', '10.36', '0', 0, 1, NULL, NULL),
(101, 'সহীহ মুসলিম - ২য় খন্ড', '', 60, 0, 'সহীহ মুসলিম  হাদিসের অন্যতম একটি গ্রন্থের নাম। ইমাম মুসলিম বিন হাজ্জাজ ইবনে মুসলিম আল কুশাইরী হলেন এই মহান গ্রন্থের সংকলক। বর্তমান ইরানের বৃহত্তর খোরাসান প্রদেশের নিশাপুরে ২০৪ হিজরিতে তিনি জন্মগ্রহণ করেন। মৃত্যুবরণ করেন ২৬১ হিজরির ২৬ শে রজব এবং নিশাপুরেই তাকে দাফন করা হয়।', '6', 'http://xeroxtree.com/pdf/muslim_02.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/711_1.jpg', 'বাংলাদেশ ইসলামিক সেন্টার', '13.23', '0', 0, 0, NULL, NULL),
