(105, 'সহীহ মুসলিম - ৬ষ্ঠ খন্ড', '', 60, 0, 'সহীহ মুসলিম  হাদিসের অন্যতম একটি গ্রন্থের নাম। ইমাম মুসলিম বিন হাজ্জাজ ইবনে মুসলিম আল কুশাইরী হলেন এই মহান গ্রন্থের সংকলক। বর্তমান ইরানের বৃহত্তর খোরাসান প্রদেশের নিশাপুরে ২০৪ হিজরিতে তিনি জন্মগ্রহণ করেন। মৃত্যুবরণ করেন ২৬১ হিজরির ২৬ শে রজব এবং নিশাপুরেই তাকে দাফন করা হয়।', '6', 'http://xeroxtree.com/pdf/muslim_06.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/715_1.jpg', 'বাংলাদেশ ইসলামিক সেন্টার', '14.38', '0', 0, 0, NULL, NULL),
(106, 'সহীহ মুসলিম - ৭ম খন্ড', '', 60, 0, 'সহীহ মুসলিম  হাদিসের অন্যতম একটি গ্রন্থের নাম। ইমাম মুসলিম বিন হাজ্জাজ ইবনে মুসলিম আল কুশাইরী হলেন এই মহান গ্রন্থের সংকলক। বর্তমান ইরানের বৃহত্তর খোরাসান প্রদেশের নিশাপুরে ২০৪ হিজরিতে তিনি জন্মগ্রহণ করেন। মৃত্যুবরণ করেন ২৬১ হিজরির ২৬ শে রজব এবং নিশাপুরেই তাকে দাফন করা হয়।', '6', 'http://xeroxtree.com/pdf/muslim_07.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/716_1.jpg', 'বাংলাদেশ ইসলামিক সেন্টার', '16.33', '0', 0, 0, NULL, NULL),
(107, 'সহীহ মুসলিম - ৮ম খন্ড', '', 60, 0, 'সহীহ মুসলিম  হাদিসের অন্যতম একটি গ্রন্থের নাম। ইমাম মুসলিম বিন হাজ্জাজ ইবনে মুসলিম আল কুশাইরী হলেন এই মহান গ্রন্থের সংকলক। বর্তমান ইরানের বৃহত্তর খোরাসান প্রদেশের নিশাপুরে ২০৪ হিজরিতে তিনি জন্মগ্রহণ করেন। মৃত্যুবরণ করেন ২৬১ হিজরির ২৬ শে রজব এবং নিশাপুরেই তাকে দাফন করা হয়।', '6', 'http://xeroxtree.com/pdf/muslim_08.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/717_1.jpg', 'বাংলাদেশ ইসলামিক সেন্টার', '16.69', '0', 0, 1, NULL, NULL),
(115, 'আসান ফেকাহ - ১ম খন্ড', '', 61, 0, 'বর্তমান মুসলিম বিশ্বে ইলমে ফেকাহের চারটি মত প্রচলিত রয়েছে, তা হলো হানাফী, মালেকী, শাফেয়ী, ও হাম্বলী। এ ছাড়াও ভিন্ন একটি মতেরও অনুসারী রয়েছে যাঁরা উপরোক্ত চার মতের কারো অনুসরণ করেন না। তাঁরা ‘সালাফী‘ বা আহলে হাদীস‘ নামে পরিচিত। উপরোক্ত সব মতই সঠিক। সব মতের ভিত্তিই কুরআন ও হাদীসের উপর প্রতিষ্ঠিত। সকলেরই প্রচেষ্টা ছিল কুরআন ও হাদীসের সার্বিক অনুসরণ। উপরোল্লেখিত মতপার্থক্যের কারণে একে অন্যের উপর দোষারোপ করা একে অন্যকে দ্বীন থেকে খারেজ মনে করে মুসলিম উম্মার ঐক্যে ফাটল সৃষ্টির প্রচেষ্টা কোন মুখলেস হকপন্থীর কাজ হতে পারে না।', '11', 'http://xeroxtree.com/pdf/asan_fekah_01.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201410/278_1.jpg', 'আধুনিক প্রকাশনী', '6.65', '0', 0, 8, NULL, NULL),
(116, 'আসান ফেকাহ - ২য় খন্ড', '', 61, 0, 'বর্তমানে মুসলিম বিশ্বে ইলমে ফিকাহের চারটি মত প্রচলিত রয়েছে, তাহলো হানাফী, মালেকী, শাফেয়ী ও হাম্বলী। এছাড়াও ভিন্ন একটি মতেরও অনুসারী রয়েছে যাঁরা উপরোক্ত চার মতের কারো অনুসরণ করেন না। তাঁরা সরাসরি কুরআন হাদীসের অনুসরণের পক্ষপাতী। তাঁরা সালাফী বা আহলে হাদীস নামে পরিচিত। উপরোক্ত সব মতই সঠিক। সব মতের ভিত্তিই কুরআন ও হাদীসের উপর প্রতিষ্ঠিত। সকলেরই প্রচেষ্টা ছিল কুরআন ও হাদীসের সার্বিক অনুসরণ। উপরোল্লিখিত মতপার্থক্যের কারণে একে অন্যের উপর দোষারোপ করা একে অন্যক দীন থেকে খারিজ মনে করে মুসলিম উম্মার ঐক্যে ফাটল সৃষ্টির প্রচেষ্টা কোনো মুখলেস হকপন্থীর কাজ হতে পারে না।', '11', 'http://xeroxtree.com/pdf/asan_fekah_02.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201410/279_1.jpg', 'আধুনিক প্রকাশনী', '5.94', '0', 0, 2, NULL, NULL),
(117, 'ফিকহুস সুন্নাহ ১ম খন্ড', '', 62, 0, 'ইসলামী জ্ঞানের নির্যাস হলো ফিকহ। মুসলিমদের জীবন চলার পূর্ণাঙ্গ বিধানগুলোর চুম্বক অংশ লিপিবদ্ধ থাকে ফিকহ। ফিকহুল ইসলামী রচিত হয় কুরআন ও সুন্নাহর আলোকে।', '11', 'http://xeroxtree.com/pdf/fikahus_sunnah_1.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201804/1712_1.jpg', 'শতাব্দী প্রকাশনী', '22.42', '0', 0, 3, NULL, NULL),
(118, 'ফিকহুস সুন্নাহ ২য় খন্ড', '', 62, 0, 'ইসলামী জ্ঞানের নির্যাস হলো ফিকহ। মুসলিমদের জীবন চলার পূর্ণাঙ্গ বিধানগুলোর চুম্বক অংশ লিপিবদ্ধ থাকে ফিকহ। ফিকহুল ইসলামী রচিত হয় কুরআন ও সুন্নাহর আলোকে।', '11', 'http://xeroxtree.com/pdf/fikhus_sunnah_ditio_khando.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201705/1713_1.jpg', 'শতাব্দী প্রকাশনী', '16.88', '0', 0, 1, NULL, NULL),
(119, 'ইসলাম পরিচিতি', '', 53, 0, 'ইসলাম পরিচিতি: সাইয়্যেদ আবুল আলা মউদুদি রা. এর একটি প্রসিদ্ধ গন্থ। এ গ্রন্থে তিনি ইসলামের পূর্ণাঙ্গ ও সহজসরল প্রকৃতি চমৎকারভাবে উপস্থাপনের প্রয়াস পেয়েছেন। ইসলামের মৌলিক উপাদানগুলোর আন্তরসম্পর্কও তিনি সুন্দরভাবে তুলে ধরেছেন এ বইটিতে।', '18', 'http://xeroxtree.com/pdf/islam_poriciti.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201410/307_1.jpg', 'আধুনিক প্রকাশনী', '3.34', '0', 0, 2, NULL, NULL),
(120, 'হেদায়াত', '', 53, 0, '১৯৫১ সালের ১৩ই নভেম্বর করাচিতে অনুষ্ঠিত জামায়াত ইসলামীর বার্ষিক সম্মেলনের শেষ অধিবেশনে কর্মীদের উদ্দেশ্যে প্রদত্ত বক্তৃতা।', '18', 'https://shibircloud.com/pdf/hedayat_v2.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202206/588_1.png', 'আধুনিক প্রকাশনী', '2.03', '0', 0, 9, NULL, NULL),
(121, 'ইসলাম ও জাহেলিয়াত', '', 53, 0, '[১৯৪১ সালের ২৩ ফেব্রুয়ারি পেশোয়ার ইসলামিয়া কলেজের মজলিশে ইসলামিয়াতের সভায় ভাষণ দেয়ার জন্যে মাওলানাকে আমন্ত্রণ জানানো হয়। এটি সে সভায় প্রদত্ত ভাষণ, যা পরবর্তীকালে ‘ইসলাম ও জাহেলিয়াত’ শিরোনামে পুস্তিকারে প্রকাশিত হয়।]', '8', 'http://xeroxtree.com/pdf/islam_o_jahiliyat.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/592_1.jpg', 'আধুনিক প্রকাশনী', '0.8', '0', 0, 1, NULL, NULL),
(122, 'সত্যের সাক্ষ্য', '', 24, 0, 'এটি মাওলানা মওদূদীর (রঃ) একটি ভাষণ। ১৯৪৬ সালের ৩০শে ডিসেম্বর লাহোর জামায়াতে ইসলামীর সম্মেলনে তিনি এ ভাষণ দেন। শিয়ালকোট সংলগ্ন মুরাদপুরে সম্মেলনটি অনুষ্ঠিত হয়।', '7', 'https://www.jamaat-e-islami.org/publication/file/56_shotter_shakkho.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202206/328_1.png', 'জামায়াতে ইসলামী বাংলাদেশ', '1.15', '0', 0, 6, NULL, NULL),
(123, 'ভাঙ্গা ও গড়া', '', 53, 0, 'প্রশংসা ও কৃতজ্ঞতা সবই আল্লাহর জন্য। তিনি আমাদেরকে সূষ্টি করেছেন। আমাদেরকে জ্ঞান, বুদ্ধি ও বুঝবার শক্তি এবং ভাল-মন্দের মধ্যে পার্থক্য করার ক্ষমতাও দিয়েছেন।', '7', 'https://jamaat-e-islami.org/en/publication/file/59_vanga_o_gora.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202206/646_1.png', 'আধুনিক প্রকাশনী', '0.8', '0', 0, 2, NULL, NULL),
(124, 'ইসলামী দাওয়াত ও কর্মনীতি', '', 53, 0, '[পুস্তিকাটি মূলত ১৯৪৫ ইংরেজী সনের ১৯ শে এপ্রিল দারুল ইসলাম পাঠানকোটে অনুষ্ঠিত জামায়াতে ইসলামীর বার্ষিক সম্মেলনে প্রদত্ত তৎকালীন আমীরে জামায়াতে সাইয়েদ আবুল আ’লা মওদূদীর ভাষণ]', '18', 'https://www.jamaat-e-islami.org/publication/file/42_islami_daoyat_o_kormoniti.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202206/605_1.png', 'জামায়াতে ইসলামী বাংলাদেশ', '2.56', '0', 0, 0, NULL, NULL),
(125, 'ইসলামী আন্দোলনঃ সাফল্যের শর্তাবলী', '', 53, 0, 'সর্বপ্রথম ভালোভাবে উপলব্ধি করতে হবে, আমাদের জাতির মধ্যে একটি আদর্শ ইসলামী সমাজ প্রতিষ্ঠার আকাঙ্খার মোটেই অভাব নেই। আসল অভাব আগ্রহ ও উদ্যোগ গ্রহণের এবং তার চাইতে বেশী অভাব যোগ্যতার। এ কাজের জন্য যে মৌলিক গুণাবলীর প্রয়োজন অধিকাংশ লোকের মধ্যে তা অনুপস্থিত।', '7', 'https://shibircloud.com/pdf/islami_andolon_safoller_sortaboli_v2.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202206/310_1.png', 'আই সি এস পাবলিকেশন', '2.76', '0', 0, 6, NULL, NULL),
(126, 'ইকামাতে দ্বীন', '', 58, 0, '১৯৭৮ সালে ‘বাংলাদেশ ইসলামী আন্দোলন’ নামে প্রথম প্রকাশিত এবং ১৯৮১ সালের তৃতীয় সংস্করণ থেকে “ইসলামী ঐক্য ইসলামী আন্দোলন” নামে আমার লেখা পুস্তিকাটিতে চিন্তা-প্রবাহের যে সিরিজ প্রকাশিত হয় তারই দ্বিতীয় সিরিজ প্রথম সিরিজে দেখান হয়েছে যে, আল্লাহ পাকের মেহেরবানীতে বাংলাদেশে ইসলামের খেদমত বিভিন্নভাবে যথেষ্ট হচ্ছে। ', '7', 'https://www.jamaat-e-islami.org/publication/file/33_ikamate_din.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202206/306_1.png', 'আধুনিক প্রকাশনী', '2.76', '0', 0, 4, NULL, NULL),
(127, 'চরিত্র গঠনের মৌলিক উপাদান', '', 31, 0, 'মানুষের তৎপরতা যত বৃদ্ধি পায়, যত অধিক গুরুত্ব অর্জন করে, সেখানে শয়তানের হস্তক্ষেপও ততই ব্যাপকতর হতে থাকে। এদিক দিয়ে বর্তমান যুগ উল্লেখযোগ্য বৈশিষ্ট্যের অধিকারী। এ যুগে একদিকে পাশ্চাত্যের বস্তুবাদী ও স্বার্থপূজারী সভ্যতা আমাদের জাতির নৈতিক পতনকে চরম পর্যায়ে উপনীত করেছে, অন্যদিকে চলছে সমাজতন্ত্রের নাস্তিক্যবাদী চিন্তার হামলা। ', '8', 'https://www.jamaat-e-islami.org/publication/file/29_choritro_gothoner_moulik_upadan.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202206/28_1.png', 'আই সি এস পাবলিকেশন', '0.7', '0', 0, 5, NULL, NULL),
(128, 'ইসলামী আন্দোলন ও সংগঠন', '', 63, 0, 'আন্দোলন, Movement এবং حركة (হারাকাতুন) এখন একটা রাজনৈতিক পরিভাষা হিসেবেই প্রচলিত। যার সাধারণ অর্থ কোন দাবী -দাওয়া প্রতিষ্ঠার জন্যে এবং কোন কিছু রদ বা বাতিল করার জন্যে কিছু লোকের সংঘবদ্ধ নড়াচড়া বা উদ্যোগ গ্রহণ করা। এর ব্যাপক ও সামগ্রিক রূপ হলো প্রতিষ্ঠিত কোন কিছুকে অপসারণ করে সেখানে নতুন কিছু কায়েম বা চালু করার উদ্দেশ্যে পরিচালিত সংঘবদ্ধ প্রচেষ্টা, প্রাণান্তকর চেষ্টা।', '7', 'https://www.jamaat-e-islami.org/publication/file/37_islami_andolon_o_songgothon.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201407/19_1.png', 'জামায়াতে ইসলামী বাংলাদেশ', '2.91', '0', 0, 1, NULL, NULL),
(129, 'পলাশী থেকে বাংলাদেশ', '', 58, 0, '১৯৭১ সালে বাংলাদেশ নামে যে নতুন একটি স্বাধীন রাষ্ট্র দুনিয়ার মানচিত্রে স্থান লাভ করল, তার ইতিহাস অনেক প্রাচীন। এখানে বিস্তারিত ইতিহাস আলোচনা উদ্দেশ্যে নয়। এজন্য প্রাচীন কাল থেকে আলোচনার অবকাশ এখানে নেই। তাই বৃটিশ শাসনের সূচনা থেকেই শুরু করছি।', '28', 'https://xeroxtree.com/pdf/polashi_theke_bangladesh.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/631_1.jpg', 'অজানা প্রকাশনী', '1.08', '0', 0, 10, NULL, NULL),
(130, 'তাওহীদ রিসালাত ও আখেরাত', '', 53, 0, 'বড় বড় শহর-নগরে আমরা দেখতে পাই, শত শত কারখানা বৈদ্যুতিক শক্তির সাহায্যে চলছে। রেল ও ট্রাম-গাড়ি তীব্রভাবে ধাবমান। সন্ধ্যার সময় হাজার হাজার বিজলী বাতি জ্বলে উঠে, গ্রীষ্মকালে প্রায় ঘরে বৈদ্যুতিক পাখা চলে। কিন্তু এতোসব কাজ দেখে আমাদের মনে যেমন কোনো বিস্ময়ের উদ্রেক হয় না, তেমনি এসব জিনিস উজ্জ্বল ও তীব্র গতিসম্পন্ন হওয়ার মূল কারণ নিয়ে আমাদের মধ্যে কোনোরূপ মতবৈষম্যেরও সৃষ্টি হয় না। এর কারণ কি?………', '8', 'http://xeroxtree.com/pdf/taohid_risalat_o_akhirat.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201410/330_1.jpg', 'আধুনিক প্রকাশনী', '1.11', '0', 0, 2, NULL, NULL),
(131, 'একমাত্র ধর্ম', '', 53, 0, '[পুস্তিকাটি মূলত মাওলানার একটি ভাষণ। ১৯৪৩ সালের ২১শে মার্চ দিল্লীর ‘জামেয়া ইসলামীয়ায়’ মাওলানা এ ভাষণ প্রদান করেন।]', '18', 'http://xeroxtree.com/pdf/ekmatro_dhormo.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201501/583_1.jpg', 'আধুনিক প্রকাশনী', '1.10', '0', 0, 0, NULL, NULL),
(132, 'ইসলামী আন্দোলনের নৈতিক ভিত্তি', '', 53, 0, '[পুস্তিকাটি মূলত মাওলানা মওদূদীর একটি ভাষণ। ১৯৪৫ সালের ২১ এপ্রিল পূর্ব পাঞ্জাবস্থ পাঠান কোর্টের ‘দারুল ইসলামে’ নিখিল ভারত জামায়াতে ইসলামীর যে সম্মেলন অনুষ্ঠিত হয়, সে সম্মেলনের সমাপ্তি অধিবেশনে মাওলানা এ ভাষণটি প্রদান করেন।]', '7', 'https://xeroxtree.com/pdf/islami_andoloner_noitik_vitti.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202206/312_1.png', 'আধুনিক প্রকাশনী', '1.54', '0', 0, 5, NULL, NULL),
(133, 'ইসলামের জীবন পদ্ধতি', '', 53, 0, 'মানব চরিত মুলত এমন এক বিশ্বব্যাপী শাশ্বত সত্য যা দুনিয়ার সকল মানুষই অবগত আছে।', '8', 'http://xeroxtree.com/pdf/islamer_jibon_poddhoti.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201608/1010_1.JPG', 'আধুনিক প্রকাশনী', '1.41', '0', 0, 1, NULL, NULL),
(134, 'ইসলামের হাকীকত', '', 53, 0, 'এখানে আমি মুসলমানের জন্য প্রয়োজনীয় গুণনসমূহ উল্লেখ করবো। অর্থাৎ মুসলমান হওয়ার জন্য কমপক্ষে শর্ত কি আর মানুষের মধ্যে কমপক্ষে কি কি গুণ বর্তমান থাকলে তাকে মুসলমান বলা যেতে পারে; এখানে আমি সে বিষয়ে বিশেষভাবে আলোচনা করবো।', '18', 'https://shibircloud.com/pdf/islamer_haqiqat.pdf', 'https://islamicbookhouse.files.wordpress.com/2016/04/screenshot_2016-04-02-14-16-13-picsay.jpg', 'আধুনিক প্রকাশনী', '1.71', '0', 0, 11, NULL, NULL),
(135, 'যাকাতের হাকীকত', '', 53, 0, 'নামাযের পর ইসলামের সর্বাপেক্ষা গুরুত্বপূর্ণ স্তম্ভ হচ্ছে যাকাত। সাধারণত নামাযের পরই রোযা উল্লেখ করা হয় বলে অনেকের মনে এ বিশ্বাস জন্মেছে যে, নামাযের পরই বুঝি রোযার স্থান। কিন্তু কালামে পাক থেকে জানা যায় যে, নামাযের পর যাকাতই হচ্ছে সবচেয়ে বেশী গুরুত্বপূর্ণ কাজ। এ দুটি ইসলামের প্রধান স্তম্ভ-এটা বিধ্বস্ত হয়ে গেল ইসলামের প্রাসাদও ধূলিসাৎ হয়ে যায়।', '18', 'http://xeroxtree.com/pdf/zakater_hakikot.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201410/331_1.jpg', 'আধুনিক প্রকাশনী', '1.42', '0', 0, 1, NULL, NULL),
(136, 'ইনফাক ফি সাবিলিল্লাহ', '', 63, 0, 'মহাগ্রন্থ আল- কুরআনে অর্থদান বুঝানোর জন্য কয়েকটি শব্দ প্রয়োগ হতে দেখা যায় যেমন যাকাত, সদকা, কাফফারা, ফিতরা , ইতয়াম ও ইনফাক। কুরআনে ব্যবহ্রত এসব শব্দগুলোর প্রতি লক্ষ্য করলে জানা যায় যে , সদকা, ইতয়াম ও  ইনফাক শব্দটি ব্যাপক অর্থবোধক। এগুলো সর্বপ্রকার দান খয়রাত এবং আল­াহর সন্তুষ্টি অর্জনের লক্ষ্যে সর্বপ্রকার ব্যয়কেই বুঝায়; তা ফরয হোক, ওয়াজিব হোক , নফল হোক কিংবা মোস্তাহাব। কিন্তু  ফরয দান বুঝাবার জন্য কুরআন একটি স্বতন্ত্র শব্দ প্রয়োগ করেছে- আর তা হচ্ছে যাকাত।', '8', 'http://xeroxtree.com/pdf2/infaq_fi_sabilillah.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201904/3359_1.png', 'জামায়াতে ইসলামী বাংলাদেশ', '1.13', '0', 0, 1, NULL, NULL),
(137, 'পর্দা ও ইসলাম', '', 53, 0, 'আজ চার বৎসর আগে পর্দা সম্পর্কে এক ধারাবাহিক প্রবন্ধ লিখিয়াছিলাম। ‘তর্জুমানূল কুরআন’ – এর কয়েকটি সংখ্যায় তাহা প্রকাশিত হইয়াছিল। তখন আলোচনার কয়েকটি দিক ইচ্ছা করিয়া বাদ দেওয়া হইয়াছিল; কোন কোন বিষয়কে অসম্পূর্ণ রাখিতে হইয়াছিল— কারন, গ্রন্থ রচনা তখন উদ্দেশ্য ছিল না; উদ্দেশ্য ছিল একটি প্রবন্ধ রচনা— সেই সকল বিষয়কে সন্নিবেশিত করিয়া প্রয়োজনীয় সংযোজন এবং বিশ্লেষণসহ বর্তমান গ্রন্থের রূপ দেওয়া হইয়াছে।', '18', 'http://xeroxtree.com/pdf/porda_o_islam.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/632_1.jpg', 'আধুনিক প্রকাশনী', '6.82', '0', 0, 3, NULL, NULL),
(138, 'দ্বীন প্রতিষ্ঠায় মহিলাদের দায়িত্ব', '', 64, 0, '', '18', 'http://xeroxtree.com/pdf/din_protisthay_mohilader_dayitto.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201606/991_1.JPG', 'আধুনিক প্রকাশনী', '1.66', '0', 0, 2, NULL, NULL),
(139, 'হাদীসে রাসূলে তাওহীদ রিসালাত আখেরাত', '', 16, 0, 'হযরত উমর (রা.) বর্ণনা করেন, “একজন আনসার ছিল, সে যখন অনুপস্থিত থাকত, তখন আমি রাসূলুল্লাহ সাল্লাল্লাহু আলাইহি ওয়াসাল্লামের কাছে উপস্থিত থাকতাম। আমি যা শুনতাম তা ঐ ব্যক্তিকে জানাতাম। আবার আমি যখন অনুপস্থিত থাকতাম তখন ঐ ব্যক্তি রাসূলুল্লাহ রাসূলুল্লাহ সাল্লাল্লাহু আলাইহি ওয়াসাল্লাম এর কাছে উপস্থিত থাকত। সে যা শুনত তা আমাকে জানাত।” (বুখারী) সাহাবাদের মত আমাদেরও রাসূলুল্লাহ সাল্লাল্লাহু আলাইহি ওয়াসাল্লামের হাদীস জানা ও মানার আগ্রহ সৃষ্টি করতে হবে। ', '6', 'http://xeroxtree.com/pdf/taohid_risalat_akhirat.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201705/1692_1.jpg', 'শতাব্দী প্রকাশনী', '3.42', '0', 0, 1, NULL, NULL),
(140, 'হাদীস শরীফ - ১ম খন্ড', '', 40, 0, 'হাদিসের উপদেশ মুসলমানদের জীবনাচরণ ও ব্যবহারবিধির অন্যতম পথনির্দেশ। কুরআন ইসলামের মৌলিক গ্রন্থ এবং হাদিসকে অনেক সময় তার ব্যাখ্যা হিসেবেও অভিহিত করা হয়।\r\n', '6', 'http://xeroxtree.com/pdf/hadis_sharif_01.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201410/298_1.jpg', 'খায়রুন প্রকাশনী', '9.19', '0', 0, 2, NULL, NULL),
(141, 'হাদীস শরীফ - ২য় খন্ড', '', 40, 0, 'ইসলামী জীবন ব্যবস্থার অবয়ব নির্মানে হাদীস এক গুরুত্বপূর্ণ ভূমিকার অধিকারী। কুরআনী নির্দেশাবলীর মর্মার্থ অনুধাবন এবং বাস্তব জীবনে উহার যথার্থ অনুশীলনে হাদীসের প্রত্যক্ষ সহযোগিতা একান্ত অপরিহার্য। এ কারণেই যুগে যুগে দেশে দেশে হাদীসের সংকলন, অনুবাদ ও বিশ্লেষণের উপর সমধিক গুরুত্ব আরোপ করা ইয়াছে। সময়ের চাহিদা মোতাবেক হাদীসের বিন্যাস ও ভাষ্য রচনার দুরূহ কাজও অনেক মনীষী সম্পাদন করিয়াছেন।', '6', 'http://xeroxtree.com/pdf/hadis_sharif_02.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201410/299_1.jpg', 'খায়রুন প্রকাশনী', '13.09', '0', 0, 0, NULL, NULL),
(142, 'হাদীস শরীফ - ৩য় খন্ড', '', 40, 0, 'ইসলামী জীবন ব্যবস্থার অবয়ব নির্মাণে হাদীস এক গুরুত্বপূর্ণ ভূমিকার অধিকারী। কুরআনী নির্দেশাবলীর মর্মার্থ অনুধাবন এবং বাস্তব জীবনে উহার যথার্থ অনুশীলনে হাদীসের প্রত্যক্ষ সহযোগিতা একান্ত অপরিহার্য। এ কারণেই যুগে-যুগে দেশে-দেশে হাদীসের সংকলন, অনুদাবন ও বিশ্লেষণের উপর সমধিক গুরুত্ব আরোপ করা হইয়াছে। সময়ের চাহিদা মোতাবেক হাদীসের বিন্যাস ও ভাষ্য রচনার দুরূহ কাজও অনেক মনীষী সম্পাদন করিয়াছেন।', '6', 'http://xeroxtree.com/pdf/hadis_sharif_03.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201410/300_1.jpg', 'খায়রুন প্রকাশনী', '12.25', '0', 0, 1, NULL, NULL),
(143, 'জিহাদের হাকীকত', '', 53, 0, 'জিহাদ (আরবি: جهاد‎‎‎), যার অর্থ সংগ্রাম; কোনো নির্দিষ্ট উদ্দেশ্য লাভের জন্য সমগ্র শক্তি নিয়োগ করাকে বোঝানো হয়। এর আভিধানিক অর্থ পরিশ্রম,সাধনা,কষ্ট, চেষ্টা ইত্যাদি। তবে সচরাচর ইসলামী পারিভাষিক অর্থে জিহাদ কথাটি ব্যবহৃত হয়ে থাকে।', '18', 'http://bjilibrary.info/pdf/educated_syllabus/18_jihader_hakikot.pdf', 'https://www.jamaat-e-islami.org/publication/thumb/l/121_jihader_haqikat.jpg', 'জামায়াতে ইসলামী বাংলাদেশ', '0.2', '0', 0, 2, NULL, NULL),
(144, 'ইসলামী সংস্কৃতির মর্মকথা', '', 53, 0, 'পাশ্চাত্য লেখকগন এবং তাঁদের প্রভাবাধীন প্রাচ্যপণ্ডিতদেরও একটি বিরাট দল এইধারণা পোষণ করে থাকে যে, ইসলামী সংস্কৃতি তার পূর্ববর্তী সংস্কৃতিসমূহ, বিশেষত গ্রীক ও রোমান সংস্কৃতি থেকে উৎসারিত এবং যেহেতু আরবীয় মানস এই পুরনো উপাদান গুলোকে এক নতুন ভঙ্গিতে বিন্যস্ত করে এর বহিরাকৃতিকে বদলে দিয়েছে, এজন্যই এ এক স্বতন্ত্র সংস্কৃতির রুপ পরিগ্রহ করেছে।', '18', 'http://xeroxtree.com/pdf/islami_songskritir_mormokotha.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/616_1.jpg', 'আধুনিক প্রকাশনী', '7.04', '0', 0, 1, NULL, NULL),
(145, 'আল্লাহর পথে জিহাদ', '', 65, 0, 'জিহাদ (আরবি: جهاد‎‎‎), যার অর্থ সংগ্রাম; কোনো নির্দিষ্ট উদ্দেশ্য লাভের জন্য সমগ্র শক্তি নিয়োগ করাকে বোঝানো হয়। এর আভিধানিক অর্থ পরিশ্রম,সাধনা,কষ্ট, চেষ্টা ইত্যাদি। তবে সচরাচর ইসলামী পারিভাষিক অর্থে জিহাদ কথাটি ব্যবহৃত হয়ে থাকে।', '18', 'http://xeroxtree.com/pdf2/qoran_hadiser_aloke_allaher_pothe_jihad.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201907/3566_1.jpg', 'আল হেরা প্রকাশনী', '4.55', '0', 0, 3, NULL, NULL),
(146, 'সুন্নাতে রাসূলের আইনগত মর্যাদা', '', 53, 0, 'বিভিন্ন যুগে মুহাদ্দিস ও মুজাদ্দিদগণ মহানবীর (স) হাদীস ও সুন্নাহর ব্যাপারে এদের বিভ্রান্তি থেকে ইসলামী উম্মাহকে হিফাযত করার ক্ষেত্রে বিরাট বিরাট অবদান রেখে গেছেন। যুক্তি ও দলিল প্রমাণের কষ্টিপাথরে হাদীস তথা সুন্নাতে রাসূলকে আইন ও শরীয়ার ভিত্তি হিসেবে যেভাবে আল্লামা মওদূদী (রঃ) সুপ্রতিষ্ঠিত করে গেছেন, গোটা হাদীস শাস্ত্রের ইতিহাসে তাঁর এ অনুপম অবদান চিরদিন সোনালী অক্ষরে লেখা থাকবে।', '8', 'http://bjilibrary.info/pdf/educated_syllabus/29_sunnate_rasuler_aingoto_morjada.pdf', 'http://www.jamaatdhakacitynorth.org/wp-content/uploads/2019/07/65477442_1451007528425732_613772447463768064_n-350x250.jpg', 'শতাব্দী প্রকাশনী', '10.23', '0', 0, 2, NULL, NULL),
(147, 'ইসলাম ও আধুনিক অর্থনৈতিক মতবাদ', '', 53, 0, 'ইসলাম ও আধুনিক অর্থনৈতিক মতবাদ বর্তমান শতকের শ্রেষ্ঠ ইসলামি চিন্তানায়ক মাওলানা সাইয়েদ আবুল আ‘লা মওদুদীর বিখ্যাত উর্দুগ্রন্থ ইসলাম আওর জাদীদ মায়াশী নযরিয়াত-এর বাংলা তরজমা ইসলাম ও আধুনিক অর্থনৈতিক মতবাদ নামে ১৯৬১ সালে প্রথম আত্মপ্রকাশ করে। এ গ্রন্থে লেখক বর্তমান দুনিয়ায় প্রচলিত যাবতীয় অর্থনৈতিক মতবাদ সম্পর্কে অত্যন্ত গভীর ও সূ²ভাবে মৌলিক আলোচনা পেশ করেছেন।', '10', 'http://xeroxtree.com/pdf2/islam_o_adhunik_orthonoitik_motobad.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201907/3653_1.jpg', 'আধুনিক প্রকাশনী', '4.09', '0', 0, 0, NULL, NULL),
(148, 'ইসলামের রাজনৈতিক মতবাদ', '', 53, 0, 'ইসলামকে সাধারনত একটি গণতান্ত্রিক মতবাদ বলে অভিহিত করা হয়। অন্তত বিগত শতকের শেষার্ধ হতে অসংখ্যবার একথাটির পুনরাবৃত্তি করা হয়েছে। কিন্তু যারা একথাটি প্রচার করে বেড়ায়, তাঁদের মধ্যে হাজারে একজন লোকও দ্বীন ইসলাম সম্পর্কে সম্যক জ্ঞানের অধিকারী নয় বলে আমার নিশ্চিত বিশ্বাস। এমনকি, ইসলামে কি ধরনের গনতন্ত্রের স্থান রয়েছে এবং এর স্বরূপই বা কি তা অনুধাবন করার জন্য তাঁরা বিন্দুমাত্রও চেষ্টা করেনি।', '7', 'http://xeroxtree.com/pdf/islamer_rajnoitik_motobad.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201410/308_1.jpg', 'আধুনিক প্রকাশনী', '1.47', '0', 0, 1, NULL, NULL),
(149, 'ইসলাম ও পাশ্চাত্য সভ্যতার দ্বন্দ্ব', '', 53, 0, 'রাষ্ট্রশাসন, বাদশাহী এবং বিজয় ও আধিপত্য দু’প্রকারঃ প্রথমটি মানসিক ও নৈতিক আধিপত্য, আর দ্বিতীয়টি রাজনৈতিক ও বস্তুগত। প্রথম শ্রেণীর আধিপত্য হলো, কোনো জাতি যখন চিন্তা, গবেষণা, আবিষ্কার ও মানসিক শক্তিতে বিপুলভাবে উন্নতি লাভ করে, তখন অন্যান্য জাতি স্বভাবতই তার চিন্তাদর্শের প্রতি ঈমান পোষণ করে। তার ধ্যান-ধারণা, ধর্মবিশ্বাস ও মতাদর্শ তাদের মন-মস্তিস্ককে আচ্ছন্ন করে ফেলে; তারই ছাঁচে তাদের মানসিকতা গড়ে উঠে।', '8', 'http://xeroxtree.com/pdf/islam_o_pascatto_sovvotar_dondo.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/595_1.jpg', 'শতাব্দী প্রকাশনী', '6.67', '0', 0, 3, NULL, NULL),
(150, 'কুরআনের চারটি মৌলিক পরিভাষা', '', 53, 0, 'ইলাহ, রব, দীন ও ইবাদত -কোরআনের পরিভাষায় এ চারটি শব্দ মৌলিক গুরুত্বের অধিকারী। কোরআনের সার্বিক দাওয়াত এই যে, একমাত্র আল্লাহ তায়ালাই একক রব ও ইলাহ; তিনি ব্যতীত অন্য কোন ইলাহ নেই, নেই কোন রব। উলুহিয়্যাত ও রুবুবিয়্যাত -এ কেউ তাঁর শরীক নেই। সুতরাং তাঁকেই তোমাদের ইলাহ ও রব মেনে নাও; তিনি ব্যতীত অন্য সকলের উলুহিয়্যাত – রুবুবিয়্যাতকে অস্বীকার করো।', '18', 'http://xeroxtree.com/pdf/quraner_4_ti_moulik_porivasa.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/634_1.jpg', 'আধুনিক প্রকাশনী', '2.83', '0', 0, 1, NULL, NULL),
(151, 'কবিরা গুনাহ', '', 66, 0, 'কবিরা গুনাহ মানুষকে জাহান্নামের দিকে নিয়ে যায়। কেউ যদি তাওবা ব্যতীত কবিরা গুনাহ নিয়ে মৃত্যুবরণ করে তবে সে কৃত গুনাহের জন্য কিয়ামতের দিন যন্ত্রণাদায়ক শস্তির সম্মুখীন হবে। (১) বেশী বেশী ওজু করা। (২) নামাজের জন্য মসজিদে যাওয়া।', '18', 'https://shibircloud.com/pdf/kobira_gunah.pdf', 'https://islamicbookhouse.files.wordpress.com/2016/01/slide1-15-picsay.jpg', 'বাংলাদেশ ইসলামিক সেন্টার', '5.35', '0', 0, 45, NULL, NULL),
(152, 'বি স্মার্ট উইথ মুহাম্মদ', '', 67, 0, 'জীবনে যারা বিশেষ কিছু হতে চান, এই বইটি তাদের জন্য। বইটির পরতে পরতে ‎রাসূল ‎ﷺ এর জীবনের এমন সব ঘটনা থাকবে, যেগুলো মানুষকে অনুপ্রেরণা দিবে দারুণভাবে। অবলীলায় তারা তাঁকে গ্রহণ করবেন অনুকরণীয় আদর্শ হিসেবে। বইটিতে তাঁর নবী হওয়ার আগের জীবন বেশি গুরুত্ব পাবে। আমরা দেখব শিশুকাল থেকে কীভাবে তিনি নিজের ব্যক্তিত্বকে গড়ে তুলেছেন।', '26', 'http://xeroxtree.com/pdf2/be_smart_with_muhammad.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201902/3191_1.jpg', 'গার্ডিয়ান পাবলিকেশনস', '4.75', '0', 0, 6, NULL, NULL),
(153, 'রাসূলুল্লাহর (সাঃ) মক্কার জীবন', '', 63, 0, 'ব্যক্তি জীবনে সত্যিকার মুসলিম হিসেবে জীবন যাপনের জন্যে যেমন তার সীরাত অধ্যয়ন ও অনুসরণ অপরিহার্য, ঠিক তেমনি ইসলামী আন্দোলন তথা ইসলামী সমাজ নির্মাণ ও প্রতিষ্ঠার প্রচেষ্টার ক্ষেত্রেও তাঁর সীরাতের উপলব্ধি ও অনুসরণ অপরিহার্য। এর কোনাে বিকল্প নেই।', '26', 'http://xeroxtree.com/pdf/rasululahr_sm_mokkar_jibon.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201805/3016_1.jpg', 'বাংলাদেশ জামায়াতে ইসলামী', '5.56', '0', 0, 4, NULL, NULL),
(154, 'রাহমাতুল্লিল আলামীন', '', 53, 0, 'মহানবী হজরত মুহাম্মদ (সা.) ‘রাহমাতুল্লিল আলামীন’ বা সমগ্র বিশ্বজগতের জন্য রহমত স্বরূপ। তিনি শুধুমাত্র মুসলমানদের জন্যই প্রেরিত হননি; তাঁকে (সা.) প্রেরণ করা হয় সমগ্র সৃষ্টিকূলের জন্য। তিনি (সা.) বিশ্ববাসীকে আজীবন যেমন শান্তির বাণী শুনিয়েছেন, বাস্তবেও বিশ্বশান্তি প্রতিষ্ঠা করতে সক্ষম হয়েছিলেন।', '26', 'http://xeroxtree.com/pdf/rahmatullil_alamin.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201711/2113_1.jpg', 'দি সাইয়েদ পাবলিশিং হাউস', '1.87', '0', 0, 3, NULL, NULL),
(155, '৩০ বছর বামফ্রন্ট-শাসনে আমরা কেমন আছি', '', 68, 0, 'গরিবের পাটি বলে চাঁদা আদায় করা, বামপন্থীদের সমালোচনা করলে কথায়। কথায় পাটি অফিসে ডেকে অপমান ও জরিমানা করা, ১৯৭৭থেকে ১৯৯৮ পর্যন্ত প্রায় একচেটিয়া ভাবে নিজের দলের বা টাকা নিয়ে শিক্ষক নিয়োগ, বামপন্থী দলগুলোর ছোটো ছোটো নেতারা নিজেদের কে মার্ক্স ও লেলিন বলে মনে করা, সবচেয়ে দুঃখের যারা পাটি ক্লাস করে ৩৪ বছর পাটি র শিক্ষায় শিক্ষিত তারাই আজ জয় শ্রীরাম স্লোগান দিচ্ছে।', '21', 'http://xeroxtree.com/pdf/30_bochorer_bamfront_shasone_amra_kemon_aci.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201708/1828_1.jpg', 'আনন্দ প্রকাশন', '2.29', '0', 0, 1, NULL, NULL),
(156, 'ছোটদের ইসলাম ০১', '', 15, 0, 'আল্লাহ আমাদের রব, আমরা তার বান্দা, আল্লাহ আমাদের সৃষ্টি করেছেন একমাত্র তাঁর ইবাদত করার জন্য। আমরা একমাত্র তাঁর ইবাদাত করব। আল্লাহর ইবাদাতে কাউকে শরীক করব না।', '16', 'https://xeroxtree.com/pdf3/Chotoder_islam_01.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202202/3738_1.jpg', 'এডুকেয়ার পাবলিকেশন্স', '31.82', '0', 0, 5, NULL, NULL),
(157, 'ছোটদের ইসলাম ০২', '', 15, 0, 'আল্লাহ আমাদের রব, আমরা তার বান্দা, আল্লাহ আমাদের সৃষ্টি করেছেন একমাত্র তাঁর ইবাদত করার জন্য। আমরা একমাত্র তাঁর ইবাদাত করব। আল্লাহর ইবাদাতে কাউকে শরীক করব না।', '16', 'https://xeroxtree.com/pdf3/Chotoder_Islam_02.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202202/3739_1.jpg', 'এডুকেয়ার পাবলিকেশন্স', '30.01', '0', 0, 2, NULL, NULL),
(158, 'কে রাজা', '', 15, 0, '', '16', 'http://xeroxtree.com/pdf/ke_raja.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201612/1192_1.jpg', 'আধুনিক প্রকাশনী', '10.76', '0', 0, 1, NULL, NULL),
(159, 'ছোটদের নজরুল', '', 39, 0, 'নজরুলের ছোটদের কবিতাগুলির মধ্যে উল্লেখযোগ্য হল পিলেপটকা, খাদু দাদু, লিচু চোর, মটকুল মাইতি, খুকি ও কাঠবিড়ালী প্রভৃতি।', '16', 'http://xeroxtree.com/pdf/chotoder_nazrul.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201611/1132_1.jpg', 'অনির্বাণ', '1.29', '0', 0, 0, NULL, NULL),
(160, 'আলোর ভুবন', '', 69, 0, '', '16', 'https://xeroxtree.com/pdf3/Alor_Vubon.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202202/3732_1.jpg', 'বাংলাদেশ কো-অপারেটিভ বুক সোসাইটি লিঃ', '6.52', '0', 0, 2, NULL, NULL),
(161, 'ইসলামী রাষ্ট্র ও সংবিধান', '', 53, 0, 'আধুনিক বিশ্বে ইসলাম এক অনন্য আদর্শ ও দুর্জয় আন্দোলন হিসেবে আত্মপ্রকাশ করেছে। ইসলামকে এখন আর সেকেলে ধর্ম আর সাম্প্রদায়িকতা বলে তুড়ি মেরে উড়িয়ে দেবার কোনো সুযোগ নেই। মুসলিনেতৃবৃন্দের অদূরদর্শিতার কারণে মুসলিম দেশগুলো যখন আধিপত্যবাদীদের করতলগত হয়, তখন তারা ইসলামকে যেভাবে ধ্বংস করে দিয়েছিল আর ইসলামকে খৃষ্টবাদ কিংবা হিন্দু/বৌদ্ধ ধর্মের মতো একটা সাম্প্রদায়িক ধর্ম হিসেবে সুচতুরভাবে অপপ্রচার করেছিল, এখন আর সেদিন নেই।', '7', 'http://xeroxtree.com/pdf/islami_rastro_o_songbidhan.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201409/172_1.JPG', 'শতাব্দী প্রকাশনী', '14.10', '0', 0, 6, NULL, NULL),
(162, 'একশ বছরের রাজনীতি', '', 70, 0, 'বাংলা চৌদ্দ শতক জাতীয় উদযাপন পরিষদের প্রকাশনা কর্মসূচীর অন্যতম বিষয় ছিল এই অঞ্চলের গত একশ বছরের রাজনীতির ওপর একটি প্রামান্য ও তথ্যবহুল গ্রন্থ প্রণয়ন করা। বিষয়টি নিঃসন্দেহে অতিশয় পরিশ্রমসাধ্য, দুরূহ কাজ। কারণ বর্তমান সময়ে ইতিহাসের উপাদান নিরাশক্তভাবে উত্থাপন করার যে ঝুঁকি রয়েছে তা অতিক্রম করতে সহজে কেউ সাহসী হয় না। ', '17', 'https://xeroxtree.com/pdf3/100_Bochorer_Rajniti.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202202/3729_1.jpg', 'বাংলাদেশ কো-অপারেটিভ বুক সোসাইটি লিঃ', '14.85', '0', 0, 15, NULL, NULL),
(163, 'আধুনিক রাষ্ট্র', '', 71, 0, 'ওয়েবারের মতে আধুনিক রাষ্ট্র নির্দিষ্ট ভূ-খণ্ডের ভেতর বলপ্রয়োগের সব মাধ্যমের উপর একচ্ছত্র নিয়ন্ত্রণ কায়েম করে। বলপ্রয়োগের এই যে একচ্ছত্র ক্ষমতা, তার আবার বিশেষ ধরনের। কারণ একে আবার আমধারণা অনুযায়ী আইনসিদ্ধ হতে হবে যে আইন ব্যক্তি স্বার্থানু্যায়ী প্রণিত নয়।', '17', 'http://xeroxtree.com/pdf2/adhunik_rastho.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201907/3461_1.jpg', 'বাংলা একাডেমী', '12.63', '0', 0, 0, NULL, NULL),
(164, 'রিপ্রেজেন্টেশনস অব দ্য ইন্টেলেকচুয়াল', '', 72, 0, 'বুদ্ধিজীবীদের আমরা মানব জাতির আদি ইতিহাসের সঙ্গে যুক্ত করতে পারি। ইতালীয় তাত্ত্বিক আন্তোনিও গ্রামসিকে ধরলে আমরা সমাজে দুই ধরনের বুদ্ধিজীবীর সাক্ষাৎ পাই। প্রথমত শিক্ষক, ধর্মপ্রচারক ও প্রশাসকের মতো ঐতিহ্যগত বুদ্ধিজীবী। অন্যদিকে আছে পুঁজিবাদী উদ্যোক্তা, শিল্প প্রযুক্তিবিদ, রাজনৈতিক অর্থনীতি বিশেষজ্ঞ, নতুন সংস্কৃতি ও নতুন বৈধ ব্যবস্থার সংগঠক তৈরিতে যারা কাজ করে। অন্যদিকে জুলিয়ান বেন্দার মতে, এরা দার্শনিক রাজাদের ক্ষুদ্র দল, যারা মানব জাতির বিবেককে নিয়ন্ত্রণ করে।', '17', 'http://xeroxtree.com/pdf2/representation_of_intelectual.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201902/3208_1.jpg', 'সংবেদ', '3.93', '0', 0, 1, NULL, NULL),
(165, 'রাজনৈতিক আদর্শ', '', 72, 0, 'সামাজিক বিজ্ঞানে রাজনৈতিক মতাদর্শ হল একটি নির্দিষ্ট নৈতিক আদর্শ, নীতি, মতবাদ ও পুরাকথার সমষ্টি। এটি বৃহৎ গোষ্টী বা শ্রেণীর প্রতিষ্ঠান ও সামাজিক আন্দোলনের প্রতীক বহন করে। একটি সমাজের গতিবিধি নির্ধারণ ও সামাজিক শৃঙ্খলা প্রদানের জন্য রাজনৈতিক এবং সাংস্কৃতিক প্রতিলিপি অর্পণ করে।', '17', 'http://xeroxtree.com/pdf2/rajnoitik_adorsho.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201902/3207_1.jpg', 'বিশ্বসাহিত্য কেন্দ্র', '3.37', '0', 0, 0, NULL, NULL),
(166, 'ওয়ান ইলেভেন- সংস্কারের রাজনীতি নয় রাজনীতির সংস্কার', '', 73, 0, 'দেশের ভবিষ্যৎ নিমজ্জিত হয়েছিল এক অন্ধকার গলিতে।', '17', 'http://xeroxtree.com/pdf/one_eleven.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201408/83_1.JPG', 'প্রফেসর প্রকাশনী', '4.98', '0', 0, 1, NULL, NULL),
(167, 'জাতীয় জীবনে মূল্যবোধের অবক্ষয়', '', 73, 0, 'বাংলাদেশের ছাত্ররাজনীতি এক সময়কার অহংকার, আজকের লজ্জা।', '17', 'http://xeroxtree.com/pdf/jatiyo_jibone_mullobodher_obokkhoy.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201408/82_1.JPG', 'প্রফেসর প্রকাশনী', '3.68', '0', 0, 0, NULL, NULL),
(168, 'দুঃসাহসিক অভিযাত্রা', '', 73, 0, 'কমিউনিজমের পতনের পর পশ্চিমাদের সামনে এখন নতুন চ্যালেঞ্জ পলিটক্যাল ইসলাম।', '17', 'http://xeroxtree.com/pdf/dusshahoshik_ovizatra.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201408/81_1.JPG', 'আরজু পাবলিকেশন্স', '5.97', '0', 0, 0, NULL, NULL),
(169, 'বাংলাদেশঃ গনতন্ত্র ও সার্বভৌমত্ব', '', 73, 0, 'পৃথিবীতে সব পরিবর্তন করা যায় কিন্তু ইতিহাস পরিবর্তন করা যায় না।', '17', 'http://xeroxtree.com/pdf/bangladesh_gonotontro_o_sarbovoumotto.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201408/80_1.jpg', 'প্রত্যয় প্রকাশনী', '7.84', '0', 0, 2, NULL, NULL),
(170, 'জামায়াতে ইসলামীর কার্যবিবরণী - ১ম খন্ড', '', 15, 0, '', '17', 'http://xeroxtree.com/pdf/jamaate_islamir_karzobiboroni_1.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201409/176_1.JPG', 'বাংলাদেশ জামায়াতে ইসলামী', '3.56', '0', 0, 2, NULL, NULL),
(171, 'জামায়াতে ইসলামীর কার্যবিবরণী - ২য় খন্ড', '', 15, 0, '', '17', 'http://xeroxtree.com/pdf/jamaate_islamir_karzobiboroni_2.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201409/177_1.JPG', 'বাংলাদেশ জামায়াতে ইসলামী', '3.21', '0', 0, 0, NULL, NULL),
(172, 'দৃশ্যপট একাত্তরঃ একুশ শতকের রাজনীতি ও আওয়ামী লীগ', '', 74, 0, '', '17', 'http://xeroxtree.com/pdf/drisshopot_ekattor.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201409/257_1.png', 'প্রীতি প্রকাশন', '48.01', '0', 0, 2, NULL, NULL),
(173, 'আওয়ামীলীগ উত্থানপর্ব ১৯৪৮-১৯৭০', '', 75, 0, 'ভারতের মুসলমানের সনাতন মনস্তত্ত্বে উম্মাহ ছিল, কিন্তু ইউরোপীয় ধাঁচের আধুনিক জাতিরাষ্ট্রের ধারণা ছিল না। দ্বিজাতিতত্ত্বের ওপর দাঁড়িয়ে তৈরি হলো পাকিস্তান। ভারতের মুসলমান সম্প্রদায়ের একটা বড় অংশ পাকিস্তান পেয়ে আবেগে ভেসে গেল। কিন্তু ধর্ম-বর্ণ-গোত্ৰনির্বিশেষে সবাইকে নিয়ে সমঅংশীদারির ভিত্তিতে একটা জাতি-রাষ্ট্র গঠনের প্রশ্ন যখন উঠল, তখন দ্বিজাতিতত্ত্বের সঙ্গে এই বাস্তবতার দ্বন্দ্ব সামনে চলে এল। দ্বিজাতিতত্ত্বের লজিকটা তখন আর কাজ করছিল না।', '17', 'http://xeroxtree.com/pdf/Aoyamilig_uthan_porbo_by_mohiuddin_ahmad2.pdf', 'https://fs.pbs.com.bd/DIR/Com/PBS/Product/ImageDetails/1901054.jpg', 'প্রথমা প্রকাশন', '7.84', '0', 0, 1, NULL, NULL),
(174, 'জাসদের উথান পতনঃ অস্থির সময়ের রাজনীতি', '', 75, 0, 'ষাট ও সত্তরের দশকে বাংলাদেশ ছিল রাজনৈতিক ঘটনাপ্রবাহে টালমাটাল । আমরা অনেকেই এই অস্থির সময়কে দেখেছি, এর নানান কর্মকাণ্ডে অংশ নিয়েছি। আমাদের আছে অনেক অর্জন, অনেক ব্যর্থতা; অনেক আনন্দ, অনেক বেদনার স্মৃতি। আমরা এই সময়টাকে ধরে রাখতে চাই, দেখতে চাই নির্মোহ দৃষ্টিতে। আমরা চাই এই সময়ের বস্তুনিষ্ঠ পর্যালোচনা।', '17', 'http://xeroxtree.com/pdf/jasoder_uthan_poton_osthir_somoyer_rajoniti_by_mohiuddin_ahmad.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201702/1522_1.jpg', 'প্রথমা প্রকাশন', '8.67', '0', 0, 1, NULL, NULL),
(175, 'বিএনপি সময়-অসময়', '', 75, 0, 'বাঙালি আবেগপ্রবণ জাতি। এ নিয়ে আমরা শ্লাঘা অনুভব করি। কিন্তু বিষয়টা সব সময় গৌরবের নয়। আবেগ যুক্তিকে ঢেকে দেয়, জিজ্ঞাসার পথ রুদ্ধ করে ফেলে, মীমাংসার পথ করে দেয় কঠিন। অতি আবেগের কারণে আমরা জাতিগতভাবে এখনো ইতিহাসমনস্ক হতে পারিনি। আর সে জন্যই আমরা ইতিহাসের চরিত্র নির্মাণ করতে ব্যর্থ হয়েছি বারবার। যা তৈরি করেছি, তা হলো কতগুলো কল্পকাহিনি বা মিথ ।l', '17', 'http://xeroxtree.com/pdf/bnp_somoy_osomoy_by_mohiuddin_ahmad.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201702/1523_1.jpg', 'প্রথমা প্রকাশন', '12.08', '0', 0, 1, NULL, NULL),
(176, 'আধুনিক বিশ্বে ইসলাম', '', 76, 0, 'বিশ্ব নেতৃত্বের আসনে ইসলামি সভ্যতা অধিষ্ঠিত হওয়ার পর বিশ্বে তার এক সুদূরপ্রসারী প্রভাব পরিলক্ষিত হয়। আগেই উল্লেখ করেছি যে, প্রাক ইসলামি যুগে বিশ্বে বিরাজমান ছিল এক নৈরাজ্যকর ও ভয়ানক পরিস্থিতি। মানবজাতি আল্লাহ বিস্মৃতি হয়ে নিমগ্ন ছিল পৌত্তলিকতার মোহে। জ্ঞান-বিজ্ঞান, শিল্প-সাহিত্য থেকে তারা ছিল অনেকটাই দূরে। কুসংস্কার আর অপসংস্কৃতিতে সয়লাব ছিল তাবৎ দুনিয়া।', '17', 'http://xeroxtree.com/pdf/adhunik_bisse_islam.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201702/1526_1.jpg', 'শতাব্দী প্রকাশনী', '4.23', '0', 0, 0, NULL, NULL),
(177, 'মুসলিম বিশ্ব সমসাময়িক সমস্যা ও চ্যালেঞ্জ', '', 51, 0, 'সমাজতন্ত্র ও সাবেক সোভিয়েত ইউনিয়ন ও পূর্ব ইউরোপের কেন্দ্রীয় পরিকল্পিত অর্থনীতির পতনের পর সকলের কাছে মানবসভ্যতার মতাদর্শিক ভবিষ্যৎ সম্পর্কে একটা জটিল প্রশ্নের উদ্ভব হয়। এটা কি ইতিহাসের যবনিকাপাত ঘটিয়ে সমাজতান্ত্রিক ব্যবস্থার চূড়ান্ত বিপর্যয় এবং পশ্চিমা পুঁজিবাদের সমর্থক অতি উৎসাহী লোকদের দাবি অনুযায়ী পাশ্চাত্যে অর্থনৈতিক ও রাজনৈতিক উদারবাদের দ্ব্যর্থহীন বিজয়, না তা ইতিহাসের গতিধারায় ক্রমঃপতনশীল অবস্থার একটি পর্যায় মাত্র?', '22', 'https://xeroxtree.com/pdf3/Muslim_Bissho_Shomossha_Challange.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202202/3745_1.jpg', 'বুকমাস্টার', '2.84', '0', 0, 3, NULL, NULL),
(178, 'মুসলিম বিশ্বের পুনর্গঠনে ছাত্র সমাজের ভূমিকা', '', 53, 0, 'স্বাধীনতা ও তার পূর্ববর্তী বাংলার ইতিহাসে যেসকল আন্দোলন-সংগ্রাম পরিচালিত হয়েছে এবং সফল হয়েছে, সবগুলোতেই ছাত্রদের ভূমিকাই ছিল মুখ্য। অন্যরা তাদের দ্বারা প্রভাবিত হয়ে সহযোগিতা করেছে মাত্র। ছাত্ররাই পারে কেবল একটি সংগ্রামকে চূড়ান্ত পর্যায়ে উপনীত করাতে। ', '22', 'https://www.xeroxtree.com/pdf3/muslim_bisser_ponorgothone_chatroder_vomika.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202105/3723_1.jpg', 'পাঠাগার ডট কম কালেকশন', '10.26', '0', 0, 2, NULL, NULL),
(179, 'মোসাদ', '', 77, 0, 'মােসাদ বিশ্বের ভয়ঙ্করতম গােয়েন্দা সংস্থা। ইসরাইলি এই সংস্থাটির দুঃসাহসিক অভিযান নিয়েই এই বই।। ইসরাইল নামক দেশটিকে যারা বিশ্ব মানচিত্র থেকে মুছে দিতে চেয়েছিলাে, মােসাদ সে-রকম ৮/১০টি দেশকে কি । নিষ্ঠুরভাবে বিধ্বস্ত ও বিপর্যস্ত করেছে তার বর্ণনা তুলে ধরা।', '22', 'http://xeroxtree.com/pdf2/mosad1.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201902/3205_1.jpg', 'আবিষ্কার', '4.26', '0', 0, 3, NULL, NULL),
(180, 'মোসাদ ২', '', 77, 0, 'মােসাদ বিশ্বের ভয়ঙ্করতম গােয়েন্দা সংস্থা। ইসরাইলি এই সংস্থাটির দুঃসাহসিক অভিযান নিয়েই এই বই।। ইসরাইল নামক দেশটিকে যারা বিশ্ব মানচিত্র থেকে মুছে দিতে চেয়েছিলাে, মােসাদ সে-রকম ৮/১০টি দেশকে কি । নিষ্ঠুরভাবে বিধ্বস্ত ও বিপর্যস্ত করেছে তার বর্ণনা তুলে ধরা।', '22', 'http://xeroxtree.com/pdf2/mosad2.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201902/3206_1.jpg', 'আবিষ্কার', '5.77', '0', 0, 0, NULL, NULL),
(181, 'বিশ্ব পরিস্থিতি ও ইসলাম', '', 70, 0, '২০২০ সালে, বিশ্বের জনসংখ্যার ১.৯১ বিলিয়নের বেশি বা প্রায় ২৪% মুসলমান ছিল। এর মধ্যে, এশিয়া-প্রশান্ত মহাসাগরীয় অঞ্চলে প্রায় ৬২%, মধ্যপ্রাচ্য-উত্তর আফ্রিকায় %, সাব-সাহারান আফ্রিকায় ১৫%, ইউরোপে প্রায় ৩% এবং আমেরিকায় ০.৩%।', '22', 'http://xeroxtree.com/pdf1/bisho_poristhiti_o_islam.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201807/3064_1.jpg', 'সাইয়েদ আবুল আলা মওদূদী রিসার্চ একাডেমী', '0.9', '0', 0, 0, NULL, NULL),
(182, 'যুক্তরাষ্ট্রে ধর্ম সমাজ', '', 70, 0, 'খ্রিস্টান ও ইহুদী ধর্মের পরে ইসলাম আমেরিকা যুক্তরাষ্ট্রের তৃতীয় বৃহত্তম ধর্ম। একটি ২০১৭ সালের সমীক্ষায় অনুমান করা হয়েছে যে ৩.৩৩ মিলিয়ন মুসলিম যুক্তরাষ্ট্রে বসবাস করছেন, যা মোট মার্কিন জনসংখ্যার প্রায় ১.১ শতাংশ।', '22', 'http://xeroxtree.com/pdf/juktorashtre_dhormo_samaj.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201801/2640_1.jpg', 'এপিক পাবলিকেশন প্রাইভেট লিমিটেড', '5.36', '0', 0, 4, NULL, NULL),
(183, 'জেরুজালেমঃ বিশ্ব মুসলিম সমস্যা', '', 17, 0, 'আল-কুদস। তথা জেরুজালেম আজ ইহুদীদের কবলে। মুসলিম বিশ্ব থেকে বিচ্ছিন্ন। মুসলমানদের প্রথম কিবলা আজ দখলদার ইহুদীদের আগ্রাসনে ধ্বংসের দ্বারপ্রান্তে উপনীত । মুসলিম বিশ্বের চোখের সামনে আজ জেরুজালেম কাঁদছে। কিন্তু এ উম্মত যেন আজ নির্বিকার, উদাসীন। ইসলামের দ্বিতীয় খলিফা হযরত উমর ফারুক (রা.)-এর সময় উদ্ধারকৃত এই কুদস আবার ক্রুসেডের আক্রমনের শিকার। আল-আকসাকে প্রায় দেড়শ বছর পদানত করে রেখেছিল ক্রুসেডারা।', '22', 'http://xeroxtree.com/pdf/jerusalem_bisso_muslim_somossa.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201711/2054_1.jpg', 'আমারবই ডট অর্গ কালেকশন', '4.11', '0', 0, 5, NULL, NULL),
(184, 'মুসলিম বিশ্বে ইসলামী আন্দোলনের কর্মপন্থা', '', 53, 0, 'র্তমান যুগে মুসলিম জাহান দুটো বিরাট অংশে বিভক্ত। প্রথমাংশে রয়েছে মুসলিম সংখ্যালঘুগণ, যারা অল্প সংখ্যক বলে রাষ্ট্রীয় ক্ষমতা ও কর্তৃত্ব রয়েছে অমুসলিমদের হাতে। আর দ্বিতীয় অংশে রয়েছে মুসলিম সংখ্যাগুরু এবং তৎসঙ্গে রাষ্ট্রীয় ক্ষমতা ও কর্তত্বও রয়েছে তাদেরই হাতে। এ দুটোর মধ্যে দ্বিতীয় অংশটির গুরুত্বই হলাে সমধিক। মুসলিম মিল্লাতের তাকদীর অনেকখানি এ সকল আযাদ মুসলিম দেশের ওপরই নির্ভর করছে—যাদেরকে তারা বর্তমানে অনুসরণ করছে এবং ভবিষ্যতেও অনুসরণ করবে। ', '22', 'http://xeroxtree.com/pdf/muslim_bishwe_islami_andoloner_kormopontha.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201612/1212_1.jpg', 'আধুনিক প্রকাশনী', '0.5', '0', 0, 2, NULL, NULL),
(185, 'ইসলামের পূনরুজ্জ্বীবনে মাওলানা মওদূদীর অবদান', '', 58, 0, 'বিংশ শতাব্দীতে এ উপমহাদেশে সাইয়েদ আবুল আলা মওদূদী (র) ছিলেন একজন প্রখ্যাত ইসলামী চিন্তাবিদ ও দার্শনিক। তাঁর ইসলামী দর্শনভিত্তিক সাহিত্য ও তাফসীর সারা বিশ্বে বিভিন্ন ভাষায় অনূদিত হয়ে ব্যাপকহারে প্রশংসিত ও সমাদৃত হয়ে আসছে। তাঁর ব্যক্তিগত জীবন ও কর্মময় জীবন সম্পর্কে জানার আগ্রহ বিপুলভাবে বৃদ্ধি পেয়েছে। মাওলানা মওদূদী জীবনীগ্রন্থ-এর পঞ্চম সংস্করণ ইতোমধ্যে শেষ হয়েছে। এখন ষষ্ঠ সংস্করণ প্রকাশিত হতে যাচ্ছে বলে আমরা মহান রাব্বুল আলামীনের অযুত শোকর আদায় করছি এবং সেই সাথে এ গ্রন্থের সম্মানিত লেখক মুহতারাম আব্বাস আল খান- যিনি এখন আর আমাদের মাঝে নেই, আমরা তাঁর রূহের মাগফেরাত কামনা করছি।', '22', 'http://xeroxtree.com/pdf/islamer_punorojjibone_maulana_maududi.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201610/1114_1.jpg', 'সাইয়েদ আবুল আলা মওদূদী রিসার্চ একাডেমী', '0.8', '0', 0, 1, NULL, NULL),
(186, 'আলী আহসান মোহাম্মদ মুজাহিদ : তুরস্ক সফর', '', 78, 0, 'মধ্য এশিয়ার যাযাবর তুর্কি জাতির লোকেরা ১১শ শতকে দেশটি দখল করে এবং এখানে সেলজুক রাজবংশের পত্তন করে। তাদের শাসনের মাধ্যমেই এই অঞ্চলের জনগণ তুর্কি ভাষা ও সংস্কৃতির সাথে মিশে যায়। ১৩শ শতকে মোঙ্গলদের আক্রমণে সেলজুক রাজবংশের পতন ঘটে। ১৩ শতকের শেষ দিকে এখানে উসমানীয় সাম্রাজ্যের পত্তন হয়।', '22', 'http://xeroxtree.com/pdf/turosko_shofor.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201609/1032_1.jpg', 'বাংলাদেশ পাবলিকেশন্স লিমিটেড', '3.25', '0', 0, 3, NULL, NULL),
(187, 'বদিউজ্জামান সাঈদ নুরসী এবং তুরস্ক', '', 76, 0, 'সাইদ নুরসি (উসমানীয় তুর্কি: بديع الزّمان سعيد النُّورسی‎; ১৮৭৬ – ২৩ মার্চ ১৯৬০), বদিউজ্জামান নামেও পরিচিত, ছিলেন একজন সুন্নি মুসলিম ধর্মতাত্ত্বিক। তিনি রিসালায়ে নূর নামক কুরআনের ব্যাখ্যা রচনা করেন। এটির আকার ছয় হাজার পৃষ্ঠার অধিক।', '22', 'http://xeroxtree.com/pdf/badiuzzaman_said_nursi_and_turkey.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201508/774_1.jpg', 'কামিয়াব প্রকাশন', '8.93', '0', 0, 4, NULL, NULL),
(188, 'মুসলিম নারীর হিজাব ও সালাতের পোশাক', '', 79, 0, 'হিজাব নিয়ে মতভেদ ও বিতর্ক যেটা আছে সেটা তৈরী করা। ইসলামি বিধানে অস্পষ্টতা বা ধোঁয়াশা থাকার কারণে বিতর্ক ও মতভেদ তৈরী হয়েছে এমনটা আদৌ নয়। হ্যাঁ, আবারো বলছি যে হিজাব কাদের জন্যে প্রযোজ্য এবং কীভাবে হিজাব পরতে হবে সে বিষয়ে ইসলামি বিধান অত্যন্ত প্রাঞ্জল ভাষায় রচনা করা হয়েছে যেখানে অস্পষ্টতা বা ধোঁয়াশা একেবারেই নেই। ', '9', 'http://xeroxtree.com/pdf/muslim_narir_hijab_o_salater_poshak.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201709/1921_1.jpg', 'বাংলাদেশ ইসলামিক সেন্টার', '2.67', '0', 0, 6, NULL, NULL),
(189, 'আল-হিদায় ১ম খণ্ড', '', 80, 0, 'আল-হিদায়া হল আল-মারঘিনানির নিজস্ব সংকলন আল-বিদায়াতুল মুবতাদির উপর সংক্ষিপ্ত ভাষ্য। যা ঘুরেফিরে আল কুদুরি ও মুহাম্মাদ আশ-শায়বানির আল-জামিউস সাগীর উপর ভিত্তি করে তৈরি হয়েছিল।', '9', 'http://xeroxtree.com/pdf/al_hidiya_1.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201801/2578_1.jpg', 'ইসলামিক ফাউন্ডেশন', '11.41', '0', 0, 1, NULL, NULL),
(190, 'আল-হিদায় ২য় খণ্ড', '', 80, 0, 'আল-হিদায়া হল আল-মারঘিনানির নিজস্ব সংকলন আল-বিদায়াতুল মুবতাদির উপর সংক্ষিপ্ত ভাষ্য। যা ঘুরেফিরে আল কুদুরি ও মুহাম্মাদ আশ-শায়বানির আল-জামিউস সাগীর উপর ভিত্তি করে তৈরি হয়েছিল।', '9', 'http://xeroxtree.com/pdf/al_hidiya_2.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201801/2579_1.jpg', 'ইসলামিক ফাউন্ডেশন', '15.41', '0', 0, 1, NULL, NULL),
(191, 'আল-হিদায় ৩য় খণ্ড', '', 80, 0, 'আল-হিদায়া হল আল-মারঘিনানির নিজস্ব সংকলন আল-বিদায়াতুল মুবতাদির উপর সংক্ষিপ্ত ভাষ্য। যা ঘুরেফিরে আল কুদুরি ও মুহাম্মাদ আশ-শায়বানির আল-জামিউস সাগীর উপর ভিত্তি করে তৈরি হয়েছিল।', '9', 'http://xeroxtree.com/pdf/al_hidiya_3.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201801/2580_1.png', 'ইসলামিক ফাউন্ডেশন', '18.34', '0', 0, 0, NULL, NULL),
(192, 'আল-হিদায় ৪র্থ খণ্ড', '', 80, 0, 'আল-হিদায়া হল আল-মারঘিনানির নিজস্ব সংকলন আল-বিদায়াতুল মুবতাদির উপর সংক্ষিপ্ত ভাষ্য। যা ঘুরেফিরে আল কুদুরি ও মুহাম্মাদ আশ-শায়বানির আল-জামিউস সাগীর উপর ভিত্তি করে তৈরি হয়েছিল।', '9', 'http://xeroxtree.com/pdf/al_hidiya_4.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201801/2581_1.jpg', 'ইসলামিক ফাউন্ডেশন', '8.16', '0', 0, 1, NULL, NULL),
(193, 'গীবত এক ঘৃণিত অপরাধ', '', 53, 0, 'গিবত বা পরনিন্দা ইসলামি শরিয়তে সম্পূর্ণরূপে হারাম ও নিষিদ্ধ। গিবত করা ও গিবত শোনা সমান অপরাধ। অনেকেই পরনিন্দাকে পাপ বা নিষিদ্ধ বলে মনেই করেন না। মদ্যপান, চুরি, ডাকাতি, ব্যভিচার ইত্যাদি থেকেও মারাত্মক ও নিকৃষ্টতম পাপ ও কবিরা গুনাহ হলো গিবত। অন্যান্য পাপ তওবা দ্বারা মাফ হয়; গিবতকারীর পাপ শুধু তওবা দ্বারা মাফ হয় না, যার গিবত করা হয়েছে, সে ব্যক্তি যদি মাফ করে, তবেই আল্লাহর কাছে মাফ পাওয়া যেতে পারে। একটি কবিরা গুনাহ কাউকে জাহান্নামে নেওয়ার জন্য যথেষ্ট।', '9', 'http://xeroxtree.com/pdf/gibot_ak_grinito_opradh.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201704/1593_1.jpg', 'শতাব্দী প্রকাশনী', '1.26', '0', 0, 5, NULL, NULL),
(194, 'ঘুরে এলাম ইরান', '', 56, 0, 'ইরান হল বিশ্বের প্রাচীনতম চলমান প্রধান সভ্যতার ভূমি, এতে খ্রিস্টপূর্ব ৭০০০ অব্দের ঐতিহাসিক ও নগর বসতির সন্ধান পাওয়া যায়। ইরানীয় উচ্চভূমির দক্ষিণ পশ্চিম ও পশ্চিম ভাগ প্রাচীন নিকট প্রাচ্য, প্রারম্ভিক ব্রোঞ্জ যুগের এলাম ও পরবর্তীকালে বিভিন্ন জাতি, যেমন কাসিতে, মানায়েন ও গুতিয়ানদের সাথে মিলিত হয়েছে।', '27', 'http://xeroxtree.com/pdf2/ghure%20elam%20iran.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201903/3280_1.jpg', 'সাফওয়ান পাবলিকেশন', '3.98', '0', 0, 1, NULL, NULL),
(195, 'পায়ে হেঁটে লন্ডন', '', 81, 0, '১৯৮১ সালে তিনি Concope (Consultative Committee of Public Enterprises) প্রতিষ্ঠা করেন। এবং দীর্ঘ পাঁচ বছর এর সেক্রেটারি | জেনারেলের দায়িত্ব পালন করেন। মানবসম্পদ, বিজ্ঞান ও প্রযুক্তি এবং জাতীয় নিরাপত্তার ক্ষেত্রে গণচেতনা জাগ্রত করার লক্ষ্যে ১৯৯৯ সালে প্রতিষ্ঠিত স্ট্র্যাটেজিক অ্যান্ড পিস\r\nস্টাডিজের প্রতিষ্ঠাতা চেয়ারম্যান হিসেবে কাজ করে যাচ্ছেন।', '27', 'http://xeroxtree.com/pdf/paye_hete_london.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201804/2855_1.jpg', 'রিদা রিসার্চ ইনস্টিটিউট', '6.66', '0', 0, 1, NULL, NULL),
(196, 'সুইডেন শান্তির দেশ', '', 81, 0, '১৮১৪ সাল থেকে সুইডেন শান্তিতে রয়েছে এবং যুদ্ধকালীন সময়ে নিরপেক্ষ বৈদেশিক নীতি গ্রহণ করেছে। প্রথম বিশ্বযুদ্ধের সময়, সুইডেন নিরপেক্ষ ছিল, তবে জার্মানদের দেশে ভ্রমণ করতে দেওয়া হয়েছিল। যুদ্ধোত্তর সমৃদ্ধি আধুনিক সুইডেনের বৈশিষ্ট্যযুক্ত সমাজকল্যাণ নীতিগুলোর ভিত্তি স্থাপন করেছিল।', '27', 'http://xeroxtree.com/pdf/suiden_shantir_desh.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201712/2426_1.jpg', 'পালাবদল পাবলিকেশন্স', '3.73', '0', 0, 1, NULL, NULL),
(197, 'বিদেশে যা দেখলাম', '', 41, 0, '', '27', 'http://xeroxtree.com/pdf/bideshe_ja_dekhlam.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201805/2993_1.jpg', 'প্রফেসরস পাবলিকেশন্স', '3.58', '0', 0, 2, NULL, NULL),
(198, 'পূর্ব-হিমালয় অঞ্চলে এজমালি আঞ্চলিক সম্পদের বণ্টন', '', 34, 0, 'উত্তর – দক্ষিণে বিস্তৃত সমান্তরাল এই ভঙ্গিল পর্বতশ্রেণীর মধ্যে পাটকাই , নাগা , লুসাই , চিন ( Chin ) , মিশমী , বরাইল , মিকির , কোহিমা , মণিপুর , মিজো , ত্রিপুরা প্রভৃতি বিশেষ উল্লেখযােগ্য । এই পর্বতশ্রেণীকে এই অঞ্চলে পূর্বের পাহাড়  বা  পূর্বাচল  বলা হয় ।', '28', 'http://xeroxtree.com/pdf/purbo_himaloy_onchole_egmaly_ancholik_sompoder_bonton.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201711/2142_1.jpg', 'পাঠাগার ডট কম কালেকশন', '1.59', '0', 0, 0, NULL, NULL),
(199, 'সাম্প্রতি সময়ের বাংলাদেশ ও ভারত সীমান্ত সমস্যা', '', 34, 0, 'সীমান্ত বাহিনী অবৈধ কার্যক্রম মোকাবেলার বাধ্যতামূলক করা হয়, বিশেষ করে মাদক চোরাচালান, যৌন কাজের জন্য মানব পাচার, এবং জাল মুদ্রা ও বিস্ফোরক পরিবহন। কিন্তু ভারতীয় সীমান্তরক্ষীদের বিরুদ্ধে ২০০০ সালের পর থেকে এ পর্যন্ত প্রায় ১,৫০০ সাধারণ ও বেসামরিক বাংলাদেশি হত্যার অভিযোগ আছে।', '28', 'http://xeroxtree.com/pdf/shamprotik_somoye_banglaesh_o_varot_simanto_somossa.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201712/2205_1.jpg', 'সেন্টার ফর স্ট্র্যাটেজিক এন্ড পিস স্টাডিজ', '2.41', '0', 0, 0, NULL, NULL),
(200, 'পার্বত্য চট্টগ্রাম ভূ-রাজনীতি ও বিপন্ন সার্বভৌমত্ব', '', 36, 0, 'বাংলাদেশ তার সৃষ্টির সূচনালগ্ন থেকেই প্রতিনিয়ত কোন না কোন সমস্যা মোকাবেলা করেই চলেছে। কখনও ফারাক্কা সমস্যা, কখন সীমান্ত সমস্যা কখনও টিপাইমুখ সমস্যা আবার কখনও বা চলছে পার্বত্য এলাকার অশান্ত পরিবেশ এমনিভাবে অসংখ্য সমস্যা। দেশটি দীর্ঘ রক্তক্ষয়ী স্বাধীনতাযুদ্ধ আর লাখ মানুষের রক্তের বিনিময়ে ১৯৭১ সালের ১৬ ডিসেম্বর স্বাধীনতা লাভ করে কিন্তু স্বাধীনতা ছিল এক খণ্ডিত ভূখণ্ড নিয়ে।', '28', 'http://xeroxtree.com/pdf/parbatto_cottrogram_vu_rajniti_o_biponno_sarbovomatto.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201712/2263_1.jpg', 'পাঠাগার ডট কম কালেকশন', '5.21', '0', 0, 0, NULL, NULL),
(201, 'দ্য দা ভিঞ্চি কোড', '', 82, 0, 'দু’হাজার বছরের পুরনো সত্যকে চিরতরে নির্মূল করার জন্যে একই দিনে চারজন বিশিষ্ট ব্যক্তিকে হত্যা করা হয়। সত্যটি জনাজানি হয়ে গেলে হাজার বছরের ইতিহাস লিখতে হবে নতুন ক’রে। সত্যটি লালন ক’রে আসছে একটি গুপ্ত সংঘ-সেই গুপ্ত সংঘের সদস্য ছিলেন আইজ্যাক নিউটন, ভিক্টর হুগো, বত্তিচেল্লি আর লিওনার্দো দা ভিঞ্চির মতো ব্যক্তি। ওদিকে উগ্র ক্যাথলিক সংগঠন ওপাস দাই সেই সত্যকে চিরতরে নির্মূল করার আগেই গুপ্তসংঘের গ্র্যান্ডমাস্টার তার ঘনিষ্ঠ একজনের কাছে হস্তান্তর ক’রে দেয় আর ঘটনাচক্রে এরকম একটি মারাত্মক মিশনে জড়িয়ে পড়ে হারভার্ডের সিম্বোলজিস্টের এক প্রফেসর।', '20', 'https://xeroxtree.com/pdf/the_da_vinci_code.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201506/757_1.JPG', 'বাতিঘর', '11.73', '0', 0, 12, NULL, NULL),
(202, 'THE DAVINCI CODE', '', 82, 0, 'The Da Vinci Code is a 2003 mystery thriller novel by Dan Brown. It is Brown s second novel to include the character Robert Langdon: the first was his 2000 novel Angels & Demons.', '20', 'http://xeroxtree.com/pdf2/the_davinci_code.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201907/3449_1.jpg', 'পাঠাগার ডট কম কালেকশন', '11.95', '0', 0, 0, NULL, NULL);
INSERT INTO `books` (`id`, `name`, `author`, `author_id`, `audio`, `description`, `category_id`, `url`, `image`, `publisher`, `size`, `is_feature`, `is_hide`, `d_count`, `created_at`, `updated_at`) VALUES
(203, 'Western Muslim and the Future of Islam', '', 83, 0, 'The author contends that Muslims can—indeed must—be faithful to their principles while participating fully in the civic life of Western secular societies. This book offers a vision of a new Muslim Identity that rejects the idea that Islam must be defined in opposition to the West.', '20', 'http://xeroxtree.com/pdf/west_moslim_and_the_future_of_islam.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201710/2047_1.jpg', 'Oxford University Press', '9.34', '0', 0, 2, NULL, NULL),
(204, 'আল-কাউলুন নাসীর শরহে বাংলা আল-ফাউজুল কাবীর', '', 84, 0, '', '11', 'https://xeroxtree.com/pdf3/Al_kawlun_nasir.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202202/3730_1.jpg', 'পাঠাগার ডট কম কালেকশন', '20.77', '0', 0, 4, NULL, NULL),
(205, 'সঠিক আক্বিদা ও উহার পরিপন্থী বিষয়', '', 85, 0, '', '11', 'http://xeroxtree.com/pdf1/sothik_akida_o_uhar_poripnthi_bisoy.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201807/3082_1.jpg', 'পাঠাগার ডট কম কালেকশন', '1.93', '0', 0, 2, NULL, NULL),
(206, 'ফাতাওয়ায়ে জামেয়া', '', 86, 0, 'ফতোয়া বা ফাতওয়া (আরবি: فتوى‎; বহুবচন ফাতাওয়া আরবি: فتاوى‎) হলো বিধান ও সমাধান, যা কোনো ঘটনা বা অবস্থার প্রেক্ষিতে ইসলামী শরীয়তের দলীলের আলোকে মুফতি বা ইসলামী আইন-বিশেষজ্ঞ প্রদান করে থাকেন।', '11', 'http://xeroxtree.com/pdf/foriyad.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201805/2983_1.jpg', 'পাঠাগার ডট কম কালেকশন', '0.7', '0', 0, 8, NULL, NULL),
(207, 'উসূলুল ফিকহঃ ফিকহ শাস্ত্রের মূলনীতি', '', 51, 0, 'মুসলিম চিন্তাবিদদের দ্বারা এ পর্যন্ত উদ্ভাবিত গবেষণা পদ্ধতিরমধ্যে উসূল আল ফিকাহকেই সবচেয়ে গুরুত্বপূর্ণ বিজ্ঞান হিসেবে যথাযথভাবে বিবেচনা করা হয়। অবশ্য ইসলামী জ্ঞানের সকল শাখা সমূহের নিখাদ ভিত্তি হিসেবে উসূল আল ফিকহ শুধুমাত্র ইসলামী সভ্যতার উপকার সাধনই করেনি বরং সামগ্রিকভাবে বিশ্ব সভ্যতার বুদ্ধিবৃত্তিক মানোন্নয়নেও অবদান রেখেছে।', '11', 'https://xeroxtree.com/pdf/usulul_fikah.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201711/2061_1.jpg', 'দি পাইওনিয়ার', '1.67', '0', 0, 7, NULL, NULL),
(208, 'ফতওয়া গুরুত্ব প্রয়োজন', '', 15, 0, 'আল্লাহ মানবজাতিকে কেবল তাঁরই ইবাদত করার জন্য সৃষ্টি করেছেন (যারিয়াত ৫৬)। মানব সৃষ্টির এই মৌলিক উদ্দেশ্য বাস্তবায়ন এবং মানবজাতির পার্থিব জীবন পরিচালনার জন্য আল্লাহ রাববুল আলামীন যুগে যুগে অসংখ্য নবী-রাসূল প্রেরণ করেছেন, যাদের মাধ্যমে তিনি মানবসমাজের জন্য প্রেরণ করেছেন আসমানী কিতাব তথা স্বীয় বিধি-বিধান। সৃষ্টিকর্তা প্রদত্ত দায়-দায়িত্ব পালন করতে হলে মানুষকে অবশ্যই এ সকল আসমানী নির্দেশ সম্পর্কে অবগত হতে হয়।', '11', 'http://xeroxtree.com/pdf/fatuyar_gurutt.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201704/1674_1.jpg', 'বাংলাদেশ ইসলামিক ল রিসার্চ এন্ড লিগ্যাল এইড সেন্টার', '2.04', '0', 0, 5, NULL, NULL),
(209, 'ইসলামী শরিয়া মূলনীতি বিভ্রান্তি ও সঠিক পথ', '', 16, 0, 'শরিয়ত হলো আল্লাহ ও তাঁর রাসূল হযরত মুহাম্মাদ (সঃ) এর দিকনির্দেশনার সমষ্টি। অতএব, শরিয়তের প্রধান বা মৌলিক উৎস দুইটি। যথা- আল্লাহর বাণী কুরআন ও তাঁর রাসূল হযরত মুহাম্মাদ (সঃ) এর বাণী, কর্ম ও মৌনসম্মতি (সুন্নাহ)। পরবর্তীতে কুরআন ও সুন্নাহর স্বীকৃতি ও নির্দেশনার ভিত্তিতে শরিয়তের আরো দুইটি বিষয় নির্ধারিত হয়।', '11', 'http://xeroxtree.com/pdf/islami_shoriya_mulniti_bivravtri_o_shottik_poth.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201704/1572_1.jpg', 'শতাব্দী প্রকাশনী', '3.39', '0', 0, 3, NULL, NULL),
(210, 'ইসলামী জীবন ব্যবস্থার মৌলিক রূপরেখা', '', 53, 0, 'ইসলামী ধর্মগ্রন্থানুযায়ী, এটি আল্লাহর নিকট একমাত্র গ্রহণযোগ্য পূর্ণাঙ্গ জীবন ব্যবস্থা। ইসলাম সর্বজনীন ধর্ম। ইসলাম শুধুমাত্র মক্কা-মদিনা বা আরব দেশগুলোর জন্য নয় বরং ইসলাম পৃথিবীর সকল বর্ণ, গোত্র, জাতি এবং ধনী-গরীব, সাদা-কালো ও আরব-অনারব সকল মানুষের জন্যই প্রেরিত।', '11', 'http://xeroxtree.com/pdf/islami_jibon_babostar_mowlik_ruprekha.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201703/1559_1.jpg', 'শতাব্দী প্রকাশনী', '10.30', '0', 0, 3, NULL, NULL),
(211, 'আল-ফিকহুল আকবর', '', 28, 0, 'আল ফিকহুল আকবর (আরবিঃ الفقه الأكبر) একটি আদি ইসলামি আকিদা বিষয়ক গ্রন্থ। ইমাম আবু হানিফার লিখিত গ্রন্থগুলোর মধ্যে অক্ষত রয়ে যাওয়া এটি অন্যতম একটি গ্রন্থ। এতে আল্লাহ, তার নবি-রাসুল, ফেরেশতা ও পরকালীন জীবন বিষয়ক বিশ্বাস নিয়ে আলোচনা করা হয়েছে।', '11', 'http://xeroxtree.com/pdf/al_fikhul_akbor.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201701/1517_1.jpg', 'আস-সুন্নাহ পাবলিকেশন্স', '15.80', '0', 0, 4, NULL, NULL),
(212, 'Crime Prevention In Islam', '', 51, 0, '', '12', 'http://xeroxtree.com/pdf/Crime_Prevention_inIslam.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201705/1700_1.jpg', 'বাংলাদেশ ইসলামিক ল রিসার্চ এন্ড লিগ্যাল এইড সেন্টার', '6.53', '0', 0, 2, NULL, NULL),
(213, 'স্মৃতির পাতা থেকে', '', 63, 0, 'ছাত্রজীবনে স্বৈরাচারী আইয়ুববিরোধী আন্দোলন থেকে নিয়ে স্বাধীনতা-উত্তর বাংলাদেশের প্রতিটি নিয়মতান্ত্রিক ও গণতান্ত্রিক আন্দোলনে তিনি বলিষ্ঠ ও অগ্রণী ভূমিকা পালন করেছেন। জনগণের অধিকার প্রতিষ্ঠা ও গণতান্ত্রিক আন্দোলনে মাওলানা নিজামীর বলিষ্ঠ ভূমিকা বাংলাদেশের মর্যাদাকে বহির্বিশ্বে উজ্জ্বল করেছে। ১৯৯১ এবং ২০০১ সালে মোট দুইবার তিনি বিপুল ভোটে জাতীয় সংসদ সদস্য নির্বাচিত হন। ২০০১-২০০৬ মেয়াদে তিনি বাংলাদেশের কৃষি ও শিল্পমন্ত্রী হিসেবে সর্বমহলে নিজেকে একজন সৎ, দক্ষ ও অমায়িক নেতার আসনে অধিষ্ঠিত করেছেন। সুদীর্ঘ ৪৫ বছরের রাজনৈতিক জীবনে দেশবাসীর কল্যাণে নিবেদিত মাওলানা নিজামীর ব্যক্তিত্ব দেশবাসীর হৃদয়ে তাদের প্রিয় নেতা হিসেবে স্থান করে নিয়েছে।', '23', 'http://xeroxtree.com/pdf2/sritir%20pata%20theke.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201903/3325_1.jpg', 'জামায়াতে ইসলামী বাংলাদেশ', '3.56', '0', 0, 3, NULL, NULL),
(214, 'আমার সংক্ষিপ্ত আত্ম-কাহিনী', '', 41, 0, 'আমার সন্তানেরা এবং আমার আদর্শিক কয়েকজন সাথীরা আমাকে বেশ। কিছুদিন যাবত অনুরােধ করে আসছিল যে, আমি যেন আমার আত্মজীবনী লিখি ও তা প্রকাশের ব্যবস্থা করি, যা পড়ে তারা আমার জীবনের ঘটনাসমূহ জানতে এবং তদ্বারা উপকৃত হতে পারবে। আমি তাদেরকে এই বলে আশ্বস্থ করতে চেষ্টা করলাম যে বয়সের ভারে বর্তমানে আমার দৃষ্টিশক্তি দুর্বল। ফলে আমি স্বাচ্ছন্দে লিখতে ও পড়তে পারিনা। কিন্তু তারা আমাকে আত্ম-জীবনী লিখার ব্যাপারে সহযােগিতা করার আশ্বাসও বার বার দিয়ে আসছিলাে।', '23', 'http://xeroxtree.com/pdf2/atto_kahini.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201903/3272_1.jpg', 'খেলাফত পাবলিকেশন্স', '3.08', '0', 0, 2, NULL, NULL),
(215, 'হজরত খাজা নিজামুদ্দীন আওলিয়া(রাঃ)', '', 87, 0, 'সুলতান-উল-মাশায়েখ, মেহবুব-এ-ইলাহী, শেখ খাজা সৈয়দ মুহাম্মদ নিজামুদ্দিন আউলিয়া (১২৩৮ - ৩ এপ্রিল ১৩২৫) (উর্দু: حضرت شیخ خواجہ سیّد محمد نظام الدّین اولیاء‎‎), নিজামুদ্দিন নামেও পরিচিত, হলেন ভারতীয় উপমহাদেশে চিশতিয়া তরিকার একজন প্রখ্যাত সূফি সাধক। ভারতে চিশতিয়া তরিকার অন্যতম মহান সূফি সাধকদের মধ্যে তিনি একজন।', '23', 'http://xeroxtree.com/pdf/hazrat-khwaja-nizamuddin-auliya-ra.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201802/2663_1.jpg', 'মজলিস-নাশরিয়াত-ই ইসলাম', '4.65', '0', 0, 5, NULL, NULL),
(216, 'আমার আম্মা', '', 87, 0, '', '23', 'http://xeroxtree.com/pdf/_my-mom.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201802/2659_1.jpg', 'মজলিস-নাশরিয়াত-ই ইসলাম', '3.36', '0', 0, 7, NULL, NULL),
(217, 'প্রত্যয়ের সূর্যোদয়', '', 32, 0, 'উপমহাদেশের সবকটি বড় ভাষায় যেখানে মুসলমানদের দখল আছে সেখানে নসীম হিজাযীর প্রভাব একটি অনস্বীকার্য সত্য। মুসলমানদের সাহিত্য চর্চায় তিনি একটি নতুন দিক নির্দেশনা দিয়েছেন। ইসলামের ইতিহাস থেকে তার অন্তরনিহিত প্রাণশক্তি ও চরিত্র মাধুর্য তিনি নিংড়ে বের করে এনেছেন। সবচেয়ে মজার ব্যাপার হচ্ছে, মুসলমানদের পতন যুগের ইতিহাস লিখেও তিনি ইসলাম ও মুসলমানকে পুনরুজ্জীবিত করার প্রচেষ্টা চালিয়েছেন।', '15', 'http://xeroxtree.com/pdf2/pottoyer_surjodoy.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201907/3605_1.jpg', 'বাংলা সাহিত্য পরিষদ', '12.87', '0', 0, 4, NULL, NULL),
(218, 'পনরই আগস্টের গল্প', '', 74, 0, '', '15', 'http://xeroxtree.com/pdf/ponori_aguster_golpo.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201805/2976_1.jpg', 'প্রীতি প্রকাশন', '1.21', '0', 0, 2, NULL, NULL),
(219, 'যে পারো ভুলিয়ে দাও', '', 88, 0, '', '15', 'http://xeroxtree.com/pdf/je_paro_vuliye_dao.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201804/2875_1.jpg', 'বাংলা সাহিত্য পরিষদ', '5.03', '0', 0, 2, NULL, NULL),
(220, 'দেয়াল', '', 89, 0, 'দেয়াল বাংলাদেশের প্রখ্যাত ঔপন্যাসিক হুমায়ুন আহমেদের লেখা একটি ইতিহাসাশ্রয়ী উপন্যাস যার ভিত্তি মুক্তিযুদ্ধ পরবর্তী বাংলাদেশের রাজনৈতিক ও সামাজিক প্রেক্ষাপট। এটি তার রচিত সর্বশেষ উপন্যাস যা তার মৃত্যুর এক বছর পর গ্রন্থাকারে প্রকাশিত হয়।', '15', 'http://xeroxtree.com/pdf/deyal1.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201801/2522_1.jpg', 'অন্যপ্রকাশ', '4.88', '0', 0, 4, NULL, NULL),
