(222, 'অপরাজিত', '', 32, 0, '', '15', 'http://xeroxtree.com/pdf/oparajito.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201711/2161_1.jpg', 'বাংলা সাহিত্য পরিষদ', '10.34', '0', 0, 1, NULL, NULL),
(223, 'তেত্রিশ কোটি দেবতার দেশে', '', 32, 0, '', '15', 'http://xeroxtree.com/pdf/tettris_kuti_debotar_deshe.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201709/1875_1.jpg', 'শতাব্দী প্রকাশনী', '3.06', '0', 0, 0, NULL, NULL),
(224, 'ইনফার্নো', '', 82, 0, 'ইনফার্নো (ইংরেজি: Inferno) ২০১৩ সালে প্রকাশিত মার্কিন লেখক ড্যান ব্রাউন রচিত একটি রহস্য, রোমাঞ্চ উপন্যাস। এটি তার সৃষ্ট চরিত্র রবার্ট ল্যাংডনকে নিয়ে চতুর্থ বই। এর আগের কিস্তিগুলো হল অ্যাঞ্জেলস অ্যান্ড ডিমনস, দ্য দা ভিঞ্চি কোড এবং দ্য লস্ট সিম্বল। বইটি ১৪ মে, ২০১৩ সালে ডাবলডে প্রকাশনী থেকে প্রকাশিত হয়।', '15', 'http://xeroxtree.com/pdf/inferno.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201512/927_1.JPG', 'বাতিঘর', '13.24', '0', 0, 3, NULL, NULL),
(225, 'ডিসেপশন পয়েন্ট', '', 82, 0, 'ডিসেপশন পয়েন্ট হলো ২০০১ সালে প্রকাশিত আমেরিকান লেখক ড্যান ব্রাউন এর রহস্য থ্রিলার উপন্যাস। এটি ব্রাউন এর তৃতীয় উপন্যাস এবং ২০২০ সাল পর্যন্ত রবার্ট ল্যাংডন সিরিজের বাইরে তার দ্বিতীয় এবং সর্বশেষ উপন্যাস । এটি সাইমন অ্যান্ড শুস্টার প্রকাশ করেছেন।', '15', 'http://xeroxtree.com/pdf/deception_point.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201512/926_1.JPG', 'বাতিঘর', '9.25', '0', 0, 4, NULL, NULL),
(226, 'ডিজিটাল ফরট্রেস', '', 82, 0, 'ডিজিটাল ফোরট্রেস (ইংরেজি ভাষায়: Digital Fortress) মার্কিন ঔপন্যাসিক ড্যান ব্রাউন রচিত একটি টেকনো-থ্রিলার উপন্যাস। বইটি ১৯৯৮ সালে প্রকাশিত হয়।', '15', 'http://xeroxtree.com/pdf/digital_fortress.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201512/925_1.JPG', 'অন্বেষা প্রকাশন', '7.63', '0', 0, 2, NULL, NULL),
(227, 'এঞ্জেলস এন্ড ডেমনস', '', 82, 0, 'অ্যাঞ্জেলস অ্যান্ড ডিমনস (ইংরেজি: Angels and Demons) মার্কিন ঔপন্যাসিক ড্যান ব্রাউন রচিত একটি নন-ফিকশন থ্রিলার উপন্যাস। এটি ২০০০ সালে প্রকাশিত হয়েছে এবং সর্বাধিক বিক্রিত বইগুলির অন্যতম। এই উপন্যাসেই ড্যান ব্রাউন রবার্ট ল্যাংডন-কে পাঠকের কাছে নিয়ে আসেন।', '15', 'http://xeroxtree.com/pdf/angels_and_demons.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201512/924_1.JPG', 'অন্বেষা প্রকাশন', '10.26', '0', 0, 3, NULL, NULL),
(228, 'পাহাড়ি এক লড়াকু', '', 91, 0, 'পাহাড়ি এক লড়াকু - মালিক আহমাদ সারওয়ার । পঞ্চম শ্রেণীতে পড়ুয়া আফগান শিশু আলী । আফগানিস্তানের পাহাড়ময় প্রকৃতির কোলে বাবা, মা এবং ছোট বোন সায়মাকে নিয়ে আলী যখন সুখ সৌরভে পার করছিল তার জীবন, তখন রাশিয়ার শ্বেত ভল্লুকেরা আগ্রাসন চালায় আফগানিস্তানে ।', '15', 'http://xeroxtree.com/pdf/pahari_ek_loraku.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201509/778_1.JPG', 'কিশোরকন্ঠ ফাউন্ডেশন', '7.70', '0', 0, 5, NULL, NULL),
(229, 'ইউসুফ বিন তাশফিন', '', 32, 0, 'ইউসুফ ইবনে তাশফিন নাসার আল-দীন ইবনে তালাকাকিঁ আল-আনহাজি‎ ; রাজত্ব করা আনু ১০৬১- ১১০৬) বার্বা‌র আলমোরাভিড সাম্রাজ্যের নেতা ছিলেন। তিনি মারাক্কেশ শহরের সহ-প্রতিষ্ঠাতা ছিলেন এবং সাগ্ররাজদের যুদ্ধে মুসলিম বাহিনীর নেতৃত্ব দিয়েছিলেন।', '15', 'http://xeroxtree.com/pdf/yusuf_bin_tashfin.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201504/750_1.JPG', 'প্রীতি প্রকাশন', '10.18', '0', 0, 1, NULL, NULL),
(230, 'মুহম্মদ ইবন কাসিম', '', 32, 0, 'ইমাদউদ্দিন মুহাম্মদ বিন কাসিম আল সাকাফি (আরবি: عماد الدين محمد بن القاسم الثقفي‎‎; c. ৩১ ডিসেম্বর ৬৯৫ – ১৮ জুলাই ৭১৫) ছিলেন একজন উমাইয়া সেনাপতি। সিন্ধু নদসহ সিন্ধু ও মুলতান অঞ্চল তিনি জয় করে উমাইয়া খিলাফতের অন্তর্ভুক্ত করেন। তিনি তাইফে (বর্তমান সৌদি আরব) জন্মগ্রহণ করেন।', '15', 'http://xeroxtree.com/pdf/muhammod_ibn_kashim.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201412/352_1.JPG', 'বাংলাদেশ কো-অপারেটিভ বুক সোসাইটি লিঃ', '6.92', '0', 0, 4, NULL, NULL),
(231, 'নাতিয়াতুন নবী', '', 74, 0, '', '13', 'http://xeroxtree.com/pdf2/natiyatun_nabi.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201907/3602_1.jpg', 'প্রীতি প্রকাশন', '2.14', '0', 0, 1, NULL, NULL),
(232, 'নজরুল ইসলাম: ইসলামী কবিতা', '', 92, 0, 'কাজী নজরুল ইসলাম তার সাহিত্য সৃষ্টিতে নানান কবিতা , গান, গল্প, প্রবন্ধ ইত্যাদি সংকলিত করেছেন। যদিও তিনি বিখ্যাত তার অনবদ্য কবিতাগুলির জন্য। কিন্তু তার ইসলামিক সঙ্গীত গুলিও সমানভাবে জনপ্রিয়। কাজী নজরুল ইসলাম তার ইসলামিক সঙ্গীত গুলিতে (মুসলমানদের সঙ্গীত শৈলীকে) এই ভাবধারা প্রকাশ করেছেন।', '13', 'http://xeroxtree.com/pdf2/nazrul_islam_islami_kobita.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201907/3600_1.jpg', 'ইসলামিক ফাউন্ডেশন', '9.00', '0', 0, 7, NULL, NULL),
