(242, 'সাত ডাকাত আর হাতেম তায়ী', '', 37, 0, 'ডান দিক থেকে পাতা উল্টাতে হয়, ভূলেও কেউ বাঁ দিক থেকে পাতা উল্টায় না।', '14', 'http://xeroxtree.com/pdf/shat_dakat_ar_hatem_tai.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201408/41_1.JPG', 'ঝিঙেফুল', '1.62', '0', 0, 1, NULL, NULL),
(243, 'পারের ডাক', '', 37, 0, 'রক্তে ভেজা গলি থেকে রাজপথ। রক্তের কতরূপ এখানে। কি উল্লসিত তার বিচরন।', '14', 'http://xeroxtree.com/pdf/parer_dak.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201408/40_1.JPG', 'হাতেখড়ি', '2.40', '0', 0, 3, NULL, NULL),
