(245, 'বিজ্ঞান ও জীবন বিধান', '', 40, 0, 'আধুনিক শিক্ষিত নাস্তিক্যবাদী বা অন্যান্য মতবাদের যেকোন ব্যক্তি বিজ্ঞানের সাথে ধর্মের প্রয়োজনীয়তা যতই অস্বীকার করুক না কেন, ইসলাম ধর্মের বিধান অনুযায়ী আমাদের দৈনন্দিন জীবনে পালনীয় ওযূ-গোসল, পোশাক-পরিচ্ছদ, খাদ্য-পানীয়, আচার-ব্যবহার, কেনা-বেচা ইত্যাদির সবকিছুই বিজ্ঞানসম্মত এবং বৈজ্ঞানিকভাবে এসবের উপকারিতা প্রমাণিত। যেকোন শিক্ষিত ব্যক্তি কুরআন মাজীদ ও কুতুবে সিত্তাহ (বুখারী, মুসলিম, আবুদাঊদ, তিরমিযী, নাসাঈ, ইবনু মাজাহ) খুলে উল্লিখিত বিষয়গুলি অধ্যায় ও বিষয়বস্ত্ত অনুসারে বিশ্লেষণ করে দেখতে পারেন।', '19', 'http://xeroxtree.com/pdf/bighgan_o_jibon_bidhan.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201801/2504_1.jpg', 'খায়রুন প্রকাশনী', '3.63', '0', 0, 5, NULL, NULL),
(246, 'ইসলাম ও বিজ্ঞান', '', 58, 0, '', '19', 'http://xeroxtree.com/pdf/islam_o_biggan_azam.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201609/1053_1.jpg', 'কামিয়াব প্রকাশন', '0.6', '0', 0, 5, NULL, NULL),
(247, 'কুরআন ও আধুনিক বিজ্ঞান - সংগতিপূর্ণ না সংগতিহীন', '', 93, 0, '', '19', 'http://xeroxtree.com/pdf/quran_o_adhunik_biggan.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201612/1458_1.jpg', 'দি রিসার্চ ফাউন্ডেশন ফর কুরআন এন্ড সাইন্স', '1.67', '0', 0, 8, NULL, NULL),
(248, 'কুরআন ও আধুনিক বিজ্ঞান সামঞ্জস্যপূর্ণ না অসামঞ্জস্', '', 93, 0, 'ভূপৃষ্টে মানুষের অস্তিত্বের প্রথম দিন থেকেই তারা প্রকৃতিকে বুঝতে চেয়েছে, সৃষ্টি পরিকল্পনায় নিজেদের মর্যাদা এবং জীবনের লক্ষ্য ও উদ্দেশ্য। সত্য ধারাবাহিকতায় শত শত বছরব্যাপী বিভিন্ন সভ্যতার মধ্য দিয়ে সুসংবদ্ধ ধর্ম মানবজীবনকে সুসংগঠিত এবং ইতিহাসের গতি নির্ধারণ করেছে। কিছু ধর্ম তো লিখিত পুস্তক আকারে আছে এবং অনুরাগীরা সে গুলোকে ঐশী প্রত্যাদেশ হিসেবে দাবী করে।আর কিছু ধর্ম আছে যেগুলো কেবল মাত্র মানবিক অভিজ্ঞতার ফসল। ইসলামী বিশ্বাসের মূল উৎস হচ্ছে, আল-কোরাআন। মুসলমানরা বিশ্বাস করে যে,এটা আল্লাহ পক্ষ থেকে এসেছে এবং তা গোটা মানব জাতির জন্য হেদায়েত। ', '19', 'http://xeroxtree.com/pdf/quran_o_adhunik_biggan_zakir_naik.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201610/1092_1.jpg', 'বিশ্ব প্রকাশনী', '1.30', '0', 0, 5, NULL, NULL),
(249, 'বাইবেল কোরআন ও বিজ্ঞান', '', 94, 0, 'ড. মরিস বুকাইলির বই এবং পদ্ধতি ব্যাপকভাবে বিশেষকরে দেশ বিদেশের মুসলিম ও অমুসলিম সাধারণ বা বোদ্ধা সর্বস্তরের পাঠক মহলে আলোচিত, সমালোচিত হয়েছে । একদিকে মুসলিমদের দ্বারা এটি প্রশংসিত হলেও অপরদিকে ইহুদি ও খ্রিষ্টবাদি পশ্চিমা পণ্ডিত, গবেষকরা এর সমালোচনা করেছেন ।', '19', 'http://xeroxtree.com/pdf/bible_quran_o_biggan.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201410/294_1.jpg', 'জ্ঞান বিতরনী', '6.82', '0', 0, 5, NULL, NULL),
(250, 'আল কুরআন এক মহাবিস্ময়', '', 94, 0, '', '19', 'http://xeroxtree.com/pdf/al_quran_ek_moha_bissoy.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201612/1275_1.jpg', 'বাংলাদেশ ইসলামিক সেন্টার', '2.54', '0', 0, 6, NULL, NULL),
(251, 'আল-কুরআন এক নজরে একশত চৌদ্দ সূরা', '', 9, 0, '', '5', 'http://xeroxtree.com/pdf2/al_quran-ak_nojore_aksho_sowddho_sura.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201907/3546_1.jpg', 'আহসান পাবলিকেশন্স', '1.90', '0', 0, 7, NULL, NULL),
(252, 'আরজ আলীর সমীপে', '', 95, 0, 'জগতে আলো অন্ধকারের মত, সত্য-মিথ্যার একটি লড়াই বিরাজমান ছিল, আছে এবং শেষ দিন পর্যন্ত বহাল থাকবে। জাহিলিয়াতের দ্বন্দ্বগুলো শেষ হয়ে যায়নি এবং যাবে না। এগুলো নানান সমস্যে নানান রূপে আমাদের সামনে এসে ধরা দেবে।', '18', 'http://xeroxtree.com/pdf2/arje_ali_somiphe.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201907/3547_1.jpg', 'সমকালীন প্রকাশন', '4.18', '0', 0, 8, NULL, NULL),
(253, 'প্যারাডক্সিক্যাল সাজিদ', '', 95, 0, 'প্যারাডক্সিক্যাল সাজিদ হল বাংলাদেশী লেখক আরিফ আজাদ রচিত একটি ইসলাম বিষয়ক ধারাবাহিক গ্রন্থ। এটি সাজিদ নামক চরিত্রের ভাষ্যে ইসলামী বিশ্বাসের বিরুদ্ধে বিভিন্ন প্রশ্নের উত্তরে সাজানো ছোটগল্পের সমাহার।', '18', 'https://marketbangladesh.com/images/blog/Paradoxical_Sajid.pdf', 'https://www.ahsanpublication.com/wp-content/uploads/2020/07/CamScanner-07-22-2020-18.55.22_1-600x966.jpg', 'গার্ডিয়ান পাবলিকেশনস', '2.32', '0', 0, 45, NULL, NULL),
(254, 'বেলা ফুরাবার আগে', '', 95, 0, 'জীবনটা কেমন যেন! ঘড়ির কাটার সাথে সাথে ছন্দ মিলিয়ে টিকটিক শব্দে কেটে যায় একজীবনের রঙীন সব বসন্ত। সময়ের পথ-পরিক্রমায় বাঁধা আছে আমাদের জন্ম আর মৃত্যু। সেই জন্ম আর মৃত্যুর মাঝামাঝি সময়ের যে সমষ্টি, তার নামই জীবন! কিন্তু সেই জীবনটা বড্ড অদ্ভুতুড়ে! এই জীবনে আমাদের চোখ মেলে দেখা হয়না সুবহে সাদিকের প্রথম প্রহর। ভোরের পবিত্র হাওয়া গায়ে লাগিয়ে, জীবনের সমস্ত ক্লান্তিকে ঝেড়ে ফেলে আমাদের ছুটে আসা হয়না মিনার থেকে ধেয়ে আসা আহ্বান পানে, যেখান থেকে ভেসে আসে কল্যাণের ডাক।', '8', 'https://www.fussilatbd.com/Story%20Books%202/Arif-Azad/2/%E0%A6%AC%E0%A7%87%E0%A6%B2%E0%A6%BE%20%E0%A6%AB%E0%A7%81%E0%A6%B0%E0%A6%BE%E0%A6%AC%E0%A6%BE%E0%A6%B0%20%E0%A6%86%E0%A6%97%E0%A7%87%20-%20%E0%A6%86%E0%A6%B0%E0%A6%BF%E0%A6%AB%20%E0%A6%86%E0%A6%9C%E0%A6%BE%E0%A6%A6.pdf', 'https://i0.wp.com/islamicboighor.com/wp-content/uploads/2020/01/Bela-Furabar-Agey.jpg', 'সমকালীন প্রকাশন', '30.02', '0', 0, 39, NULL, NULL),
(255, 'মনটাকে কাজ দিন', '', 58, 0, 'আপনার দেহকে শোয়া, বসা, দাঁড়ানো বা হাঁটানো ইত্যাদি যে অবস্থায়ই রাখুন, সকল অবস্থায়ই আপনার মন কাজ করতে থাকে। এমনকি দেহ যখন ঘুমে অচেতন থাকে তখনো মন কাজ করতে থাকে। সে ঘুমায় না। মনোবিজ্ঞানীরা স্বপ্নকে মনের কর্মব্যস্ততা বলে ব্যাখ্যা করেছেন। দেহ যখন ক্লান্ত শ্রান্ত হয়ে বিছানায় এলিয়ে পড়ে, মন তখন আরো সক্রিয় হয়। দেহ তখন ক্লান্ত ও হয় না, বিশ্রামও নেয়না।', '1', 'http://shibircloud.com/audio/lq/montake_kaj_din.mp3', 'https://www.icsbook.info/uploads/bookbook_image/252_Picture2.png', 'কামিয়াব প্রকাশন', '12.54', '0', 0, 9, NULL, NULL),
(256, 'তাওহীদ রেসালাত ও আখেরাত', '', 53, 0, 'বড় বড় শহর-নগরে আমরা দেখতে পাই, শত শত কারখানা বৈদ্যুতিক শক্তির সাহায্যে চলছে। রেল ও ট্রাম-গাড়ি তীব্রভাবে ধাবমান। সন্ধ্যার সময় হাজার হাজার বিজলী বাতি জ্বলে উঠে, গ্রীষ্মকালে প্রায় ঘরে বৈদ্যুতিক পাখা চলে। কিন্তু এতোসব কাজ দেখে আমাদের মনে যেমন কোনো বিস্ময়ের উদ্রেক হয় না, তেমনি এসব জিনিস উজ্জ্বল ও তীব্র গতিসম্পন্ন হওয়ার মূল কারণ নিয়ে আমাদের মধ্যে কোনোরূপ মতবৈষম্যেরও সৃষ্টি হয় না। এর কারণ কি?.........', '1', 'http://shibircloud.com/audio/lq/taohid_risalat_o_akhirat.mp3', 'https://www.icsbook.info/uploads/bookbook_image/FormatFactorytaohid.jpg', 'আধুনিক প্রকাশনী', '22.27', '0', 0, 2, NULL, NULL),
(257, 'মৃত্যু যবনিকার ওপারে', '', 96, 0, 'যেসব আকীদাহ বিশ্বাসের উপরে ঈমানের প্রাসাদ দাঁড়িয়ে আছে তার মধ্যে আখেরাতের প্রতি বিশ্বাস অন্যতম। আখেরাতের প্রতি বিশ্বাস না থাকলে, আল্লাহ্‌, রসূল, আল্লাহর কেতাব প্রভৃতির প্রতি সত্যিকার অর্থে বিশ্বাসই জন্মে না। উপরন্তু প্রবৃত্তির দাসত্ব ও শয়তানী প্ররোচনা থেকে মুক্ত হয়ে নিষ্ঠার সাথে নেক আমল করতে হলে আখেরাতের প্রতি দৃঢ় বিশ্বাস ছাড়া কিছুতেই সম্ভব নয়।', '1', 'https://shibircloud.com/audio/lq/mrittu_jobonikar_opare.mp3', 'https://www.icsbook.info/uploads/bookbook_image/256_Picture8.png', 'আধুনিক প্রকাশনী', '80', '0', 0, 23, NULL, NULL),
(258, 'ইসলামের সহজ পরিচয়', '', 58, 0, 'জনগণের এ নেতাদের উদ্দেশ্যেই আমার অতি প্রিয় বইটি উৎসর্গ করলাম। তারা যে দলের লোকই হোন, ইসলাম সম্পর্কে তাদের জানার অধিকার আছে। তারা আল্লাহকে বিশ্বাস করেন, রাসূল সা. কে ভালোবাসেন এবং কুরআন মাজীদ কে ভক্তি-শ্রদ্ধা করেন। তারা যদি ইসলামের জ্ঞান লাভ করেন, তাহলে তাদের মাধ্যমেই জনগণের মধ্যে ইসলামের আলো পৌঁছবে। যারা বই পড়তে পারেন না তাদেরকে তারাই বইটি পড়ে শোনাবেন ও বোঝাবেন।', '1', 'https://shibircloud.com/audio/lq/islamer_sohoj_poricoy.mp3', 'https://www.icsbook.info/uploads/bookbook_image/sohoj_poricoy.jpg', 'জামায়াতে ইসলামী বাংলাদেশ', '62.2', '0', 0, 21, NULL, NULL),
(259, 'মযবুত ঈমান', '', 58, 0, 'ইসলামী আন্দোলনের  ও সংগঠনে দীর্ঘ অভিজ্ঞতা থেকে আমি তীব্রভাবে উপলব্ধি করেছি যে, ইসলামকে সঠিকভাবে বুঝা সত্ত্বেও ইসলামের দাবি পূরণে অনেকেই ব্যৰ্থ হয়। ধীরে সুস্থে বিবেক-বুদ্ধি যথাযথভাবে প্রয়োগ করে ইসলামী আন্দোলনে অগ্রসর হবার পর কেউ কেউ আবার পিছিয়ে যায়। জামায়াতে ইসলামীর রুকন হিসেবে বেশ কিছুদিন সক্রিয় ভূমিকা পালনের পরও একসময় নিক্রিয় হয়ে পড়ে। ', '1', 'https://shibircloud.com/audio/lq/mojbut_iman.mp3', 'https://www.icsbook.info/uploads/bookbook_image/FormatFactoryPresentation1.jpg', 'আধুনিক প্রকাশনী', '26.78', '0', 0, 14, NULL, NULL),
(260, 'ইসলামের হাকীকত', '', 53, 0, 'এখানে আমি মুসলমানের জন্য প্রয়োজনীয় গুণনসমূহ উল্লেখ করবো। অর্থাৎ মুসলমান হওয়ার জন্য কমপক্ষে শর্ত কি আর মানুষের মধ্যে কমপক্ষে কি কি গুণ বর্তমান থাকলে তাকে মুসলমান বলা যেতে পারে; এখানে আমি সে বিষয়ে বিশেষভাবে আলোচনা করবো।', '1', 'https://shibircloud.com/audio/lq/islamer_haqiqat.mp3', 'https://www.icsbook.info/uploads/bookbook_image/islamer_haqiqat.jpg', 'আধুনিক প্রকাশনী', '25.41', '0', 0, 10, NULL, NULL),
(261, 'যাকাতের হাকীকত', '', 53, 0, 'নামাযের পর ইসলামের সর্বাপেক্ষা গুরুত্বপূর্ণ স্তম্ভ হচ্ছে যাকাত। সাধারণত নামাযের পরই রোযা উল্লেখ করা হয় বলে অনেকের মনে এ বিশ্বাস জন্মেছে যে, নামাযের পরই বুঝি রোযার স্থান। কিন্তু কালামে পাক থেকে জানা যায় যে, নামাযের পর যাকাতই হচ্ছে সবচেয়ে বেশী গুরুত্বপূর্ণ কাজ। এ দুটি ইসলামের প্রধান স্তম্ভ-এটা বিধ্বস্ত হয়ে গেলে ইসলামের প্রাসাদও ধূলিসাৎ হয়ে যায়।', '1', 'http://shibircloud.com/audio/lq/zakater_hakikot.mp3', 'https://www.icsbook.info/uploads/bookbook_image/zakat.png', 'আধুনিক প্রকাশনী', '27.89', '0', 0, 2, NULL, NULL),
(262, 'ইকামাতে দ্বীন', '', 58, 0, 'যারা নিজেদের দ্বীনি খেদমতকেই শুধু মূল্যবান মনে করে এবং অন্যদের খেদমতের কদর করে না, তাদের দ্বারা উম্মাতের মধ্যে বিভেদ ও বিদ্বেষ সৃষ্টি হবার আশংকা রয়েছে। সবারই এ ধারণা করা উচিত যে, আমরা সবাই বিভিন্ন আকারে ও পদ্ধতিতে একই মহান দ্বীনের খেদমত করছি। একথাও ইখলাসের সাথে সবারই মনে রাখা উচিত যে, উপরোক্ত সব ক’টি খেদমতই নিজ জি ক্ষেত্রে মূল্যবান ও যরুরী। যদি সবাই সবার খেদমতকে মূল্যবান বলে স্বীকার করে তাহলে দেশের ইসলামী শক্তিগুলোর মধ্যে ঐক্য ও মহব্বত সৃষ্টি হবে এবং দ্বীনের মর্যাদা আরও বাড়বে।', '1', 'http://shibircloud.com/audio/lq/ikamate_din.mp3', 'https://www.icsbook.info/uploads/bookbook_image/PWZuirhr1OxEwm2ogKk3Q8zOLR0RsdwTUIpkUxEU.png', 'আধুনিক প্রকাশনী', '41.55', '0', 0, 3, NULL, NULL),
(263, 'হজ্জের হাকীকত', '', 53, 0, 'আরবী ভাষায় ‘হজ্জ’ অর্থ যিয়ারতের সংকল্প করা। যেহেতু খানায়ে কা’বা যিয়ারত করার উদ্দেশ্যে মুসলমানরা পৃথিবীর চারদিক থেকে নির্দিষ্ট কেন্দ্রের দিকে চলে আসে, তাই এর নাম রাখা হয়েছে ‘হজ্জ’। কখন কিভাবে হজ্জের সূচনা হয়েছিল, সেই ইতিহাস অত্যন্ত চিত্তাকর্ষক এবং শিক্ষাপ্রদ। গভীর মনোযোগের সাথে সেই ইতিহাস অধ্যয়ন করলে হজ্জের কল্যাণকারিতা হৃদয়ঙ্গম করা পাঠকের জন্য সহজ হবে।', '18', 'http://xeroxtree.com/pdf/hajjer_hakikot.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201410/305_1.jpg', 'আধুনিক প্রকাশনী', '1.39', '0', 0, 1, NULL, NULL),
(264, 'হজ্জের হাকীকত', '', 53, 0, 'আরবী ভাষায় ‘হজ্জ’ অর্থ যিয়ারতের সংকল্প করা। যেহেতু খানায়ে কা’বা যিয়ারত করার উদ্দেশ্যে মুসলমানরা পৃথিবীর চারদিক থেকে নির্দিষ্ট কেন্দ্রের দিকে চলে আসে, তাই এর নাম রাখা হয়েছে ‘হজ্জ’। কখন কিভাবে হজ্জের সূচনা হয়েছিল, সেই ইতিহাস অত্যন্ত চিত্তাকর্ষক এবং শিক্ষাপ্রদ। গভীর মনোযোগের সাথে সেই ইতিহাস অধ্যয়ন করলে হজ্জের কল্যাণকারিতা হৃদয়ঙ্গম করা পাঠকের জন্য সহজ হবে।', '1', 'http://shibircloud.com/audio/lq/hajjer_hakikot.mp3', 'https://www.icsbook.info/uploads/bookbook_image/hajj.jpg', 'আধুনিক প্রকাশনী', '24.04', '0', 0, 1, NULL, NULL),
(265, 'ইসলামী রাষ্ট্র কিভাবে প্রতিষ্ঠিত হয়', '', 53, 0, 'তখনো ভারত স্বাধীন হয়নি। ভারত বিভক্ত হয়ে পাকিস্থান রাষ্ট্রের জন্ম হয়নি। কিন্তু ভারতের স্বাধীনতা লাভ আর মুসলমানদের জন্যে পৃথক আবাসভূমি এ দুটো বিষয়ই চূড়ান্তভাবে কার্যকর হবার দিকে অগ্রসর হচ্ছিল। মুসলমানরা একটি ইসলামী রাষ্ট্র প্রতিষ্ঠা করতে চাচ্ছিল, যেখানে সর্বক্ষেত্রে কার্যকর হবে কুরআন সুন্নাহ বিধান। কিন্তু যারা এই রাষ্ট্রটি প্রতিষ্ঠা করার ক্ষেত্রে নেতৃত্ব দিচ্ছিল, তাদের মধ্যে না ছিল ইসলামের যথার্থ জ্ঞান, না ছিল ইসলামী চরিত্র আর না ছিল নিরেট ইসলামী সমাজ গড়ার মন মানসিকতা। ', '1', 'http://shibircloud.com/audio/lq/islami_biplober_poth.mp3', 'https://www.icsbook.info/uploads/bookbook_image/xCTHYb8Bxcpi6vSf1WZ5hw7J1BMiA59h4og3UNuu.png', 'জামায়াতে ইসলামী বাংলাদেশ', '25.54', '0', 0, 2, NULL, NULL),
(266, 'ইসলামী আন্দোলনঃ সাফল্যের শর্তাবলী', '', 53, 0, 'সর্বপ্রথম ভালোভাবে উপলব্ধি করতে হবে, আমাদের জাতির মধ্যে একটি আদর্শ ইসলামী সমাজ প্রতিষ্ঠার আকাঙ্খার মোটেই অভাব নেই। আসল অভাব আগ্রহ ও উদ্যোগ গ্রহণের এবং তার চাইতেও বেশী অভাব যোগ্যতার। এ কাজের জন্য যে মৌলিক গুণাবলীর প্রয়োজন অধিকাংশ লোকের মধ্যে তা অনুপস্থিত।', '1', 'http://shibircloud.com/audio/lq/islami_andolon_safoller_shortaboli.mp3', 'https://www.icsbook.info/uploads/bookbook_image/MUm8eVa3slCvqfmLCEZImlfM19FdPasvc4OlAsEc.png', 'আই সি এস পাবলিকেশন', '32.52', '0', 0, 4, NULL, NULL),
(267, 'সত্যের সাক্ষ্য', '', 53, 0, 'আমাদের দাওয়াত হচ্ছে যারা প্রথমত: বংশগত মুসলমান এবং দ্বিতীয়ত: মুসলমান নয় এমন সব মানবগোষ্ঠীর প্রতি। এদের প্রত্যেকের জন্যই আমাদের কাছে বিশেষ পয়গাম রয়েছে। কিন্তু পরিতাপের বিষয় যে, এখানে শেষোক্ত দলের লোকদেরকে আমি দেখতে পাচ্ছি না। আমাদের অতীতের ভুল ও বর্তমান অবস্থার ফলেই মানব জাতির এক বিরাট অংশ আমাদের থেকে দূরে সরে গেছে। এমতাবস্থায় তাদের ও আমাদের মহান প্রভু আমাদের পথ প্রদর্শনের জন্য নবীদের মারফত যে পয়গাম পাঠিয়েছিলেন, তাকে তাদের কাছে পৌঁছানোর সুযোগ আমরা খুব কমই পেয়ে থাকি। ', '1', 'http://shibircloud.com/audio/lq/shotter_shakkho.mp3', 'https://www.icsbook.info/uploads/bookbook_image/JGPQ0gyR7jX7LI5pPWLJxbcON9q07sWdpO5Z8D5T.png', 'জামায়াতে ইসলামী বাংলাদেশ', '18.19', '0', 0, 2, NULL, NULL),
(268, 'ইসলামী আন্দোলনের কর্মীদের পারস্পরিক সম্পর্ক', '', 97, 0, 'ইতিহাস পর্যালোচনা করলে একথা দিবালোকের মতো স্পষ্ট হয়ে ওঠে যে, আম্বিয়ায়ে কিরাম সর্বদাই মানব সমাজের পুনর্বিন্যাস করেছেন। তাঁরা মানব জাতিকে এক বুনিয়াদী আদর্শের দিকে আহবান জানিয়েছেন। এবং সে আহবানে যারা সাড়া দিয়েছে, তাদেরকে এক নতুন ঐক্যসূত্রে গেঁথে দিয়েছেন। যে মানব গোষ্ঠী বিভিন্ন দল-গোত্র-খান্দানে দ্বিধাবিভক্ত হয়েছিলো, ছিলো পরস্পরের রক্ত পিপাসু ও ইজ্জতের দুশমন-এ আহবানর ফলে তারা পরস্পর পরস্পরের ভাই এবং একে অপরের ইজ্জতের সংরক্ষক বনে গেল। ', '1', 'http://shibircloud.com/audio/lq/islami_andoloner_kormider_parosparik_somporko.mp3', 'https://www.icsbook.info/uploads/bookbook_image/OXkM8kgeqtPevL9ole5mnnf67bkbkdRQEDHutdov.png', 'আই সি এস প্রকাশনী', '47.33', '0', 0, 2, NULL, NULL),
(269, 'ইসলামী আন্দোলন ও সংগঠন', '', 63, 0, 'আন্দোলন, Movementএবং حركة(হারাকাতুন) এখন একটা রাজনৈতিক পরিভাষা হিসেবেই প্রচলিত। যার সাধারণ অর্থ কোন দাবী -দাওয়া প্রতিষ্ঠার জন্যে এবং কোন কিছু রদ বা বাতিল করার জন্যে কিছু লোকের সংঘবদ্ধ নড়াচড়া বা উদ্যোগ গ্রহণ করা। এর ব্যাপক ও সামগ্রিক রূপ হলো প্রতিষ্ঠিত কোন কিছুকে অপসারণ করে সেখানে নতুন কিছু কায়েম বা চালু করার উদ্দেশ্যে পরিচালিত সংঘবদ্ধ প্রচেষ্টা, প্রাণান্তকর চেষ্টা। ', '1', 'http://shibircloud.com/audio/lq/islami_andolon_o_songgothon.mp3', 'https://www.icsbook.info/uploads/bookbook_image/4AvnUHKf335g21dP8dqN2KS2ExYrjShpfrt6XKr6.png', 'জামায়াতে ইসলামী বাংলাদেশ', '57.1', '0', 0, 1, NULL, NULL),
(270, 'ধর্ম নিরপেক্ষ মতবাদ', '', 58, 0, '‘ধর্মনিরপেক্ষতাবাদ’ ইংরেজী ‘সেকিউলারিজম’ শব্দেরই বাংলা অনুবাদ। গত আড়াই শত বছর থেকে এটা দুনিয়ার সর্বত্র একটি আদর্শের মর্যাদা লাভ করেছে। মুসলিম প্রধান দেশগুলোর মধ্যে সর্বপ্রথম কামাল পাশাই তুরস্কে এই মতবাদ প্রতিষ্ঠিত করেন। ইতিপূর্বে মুসলমানগণ এরূপ নির্লজ্জভাবে দুনিয়াময় প্রচার করে ধর্মনিরপেক্ষতাবাদের দীক্ষা গ্রহণ করেছে বলে কোন নযীর পাওয়া যায় না। এমনকি যে পাকিস্তান ধর্মভিত্তিক রাষ্ট্র হিসাবে কায়েম হয়েছিল সেখানেও এক শ্রেণীর মুসলিম নামধারী প্রভাবশালী লোক ধর্মনিরপেক্ষতার প্রকাশ্য সমর্থক। ', '1', 'https://shibircloud.com/audio/lq/dhormoniropekkho_motobad.mp3', 'https://www.icsbook.info/uploads/bookbook_image/dnm.png', 'আধুনিক প্রকাশনী', '11.7', '0', 0, 23, NULL, NULL),
(271, 'ভাঙ্গা ও গড়া', '', 53, 0, 'আমাদেরকে জ্ঞান, বুদ্ধি ও বুঝবার শক্তি এবং ভাল-মন্দের মধ্যে পার্থক্য করার ক্ষমতাও দিয়েছেন। আমাদের সঠিক পথ প্রদর্শন ও নেতৃত্ব দানের জন্য তিনি তাঁর সর্বোত্তম বান্দাদেরকে পাঠিয়েছেন। তাঁরা মানুষকে মানবতা শিক্ষা দিয়েছেন, সৎলোকের মতো জীবনযাপন করা শিখিয়েছেন এবং মানব জীবনের মূল উদ্দেশ্য সম্পর্কে অবহিত করেছেন। যে সমস্ত নিয়ম-নীতি অনুযায়ী জীবন যাপন করলে মানুষ দুনিয়ায় সুখ-শান্তি ও পরকালে মুক্তি লাভ করতে পারে তাও তারা বলে এবং করে দেখিয়ে দিয়েছেন। তাঁদের সবার উপর শান্তি বর্ষিত হোক।', '1', 'http://shibircloud.com/audio/lq/vanga_o_gora.mp3', 'https://www.icsbook.info/uploads/bookbook_image/kUTytWhDpd1Vfaa2e5shdXgCpa14gTmjxahHG8Mj.png', 'আধুনিক প্রকাশনী', '13.26', '0', 0, 6, NULL, NULL),
(272, 'হেদায়াত', '', 53, 0, 'চারদিন ব্যাপী সম্মেলনের পর এখন সকলেই এখান থেকে বিদায় নিচ্ছে। এ সম্মেলন উপলক্ষে নির্ধারিত কার্যসূচী আল্লাহর ফযলে সম্পন্ন হয়েছে এবং সেই সম্পর্কে আমরা বিশেষ অধিবেশনে মোটামুটি ভাবে পর্যলোচনাও করেছি। এখান থেকে বিদায় গ্রহণের পূর্বে আমি আমার সহকর্মী রুকন এবং মুত্তাকীগণকে (বর্তমানে সহযোগী সদস্য) আমাদের কর্মপন্থা সম্পর্কে কয়েকটি জরুরী কথা বলতে চাই; যেন তারা ভবিষ্যতে নিজেদের দায়িত্ব সঠিকভাবে পালন করতে পারেন।', '1', 'http://shibircloud.com/audio/lq/hedayat.mp3', 'https://www.icsbook.info/uploads/bookbook_image/051dU7zgTVL6EDxKrnyi42Qaa2SPXEyz2nP7QtOU.png', 'আধুনিক প্রকাশনী', '20.62', '0', 0, 2, NULL, NULL),
(273, 'একটি আদর্শবাদী দলের পতনের কারণঃ তার থেকে বাঁচার উপ', '', 96, 0, 'ইসলামী আন্দোলনের পথিকৃত মরহুম আব্বাস আলী খান রচিত একটি আদর্শবাদী দলের পতনের কারণ : তার থেকে বাঁচার উপায় বইটি ১৯৯৮ সালে প্রথম প্রকাশিত হয়। বইটির উত্তরোত্তর চাহিদার প্রেক্ষিতে সম্পূর্ণ নতুন আঙ্গিকে আবারো প্রকাশিত হলো। বর্তমান সংস্করণে প্রয়োজনীয় সংশোধন ও পরিমার্জন করা হয়েছে।ইসলামী সমাজ ব্যবস্থা প্রতিষ্ঠা করার জন্য দরকার একটি আদর্শবাদী মজবুত সংগঠন বা দল। এ দল এমনিতে গড়ে উঠে না, এ জন্য প্রয়োজন ত্যাগ-তিতিক্ষা, কুরবানী। ', '1', 'https://shibircloud.com/audio/lq/ekti_adorshobadi_doler_potoner_karon.mp3', 'https://www.icsbook.info/uploads/bookbook_image/MpSwUWUXVSCJGAIK0nThi6Nl99ohdTbS3Yd5U697.png', 'জামায়াতে ইসলামী বাংলাদেশ', '15.88', '0', 0, 14, NULL, NULL),
(274, 'ইসলামী সংগঠন', '', 22, 0, 'নেতা ছাড়া সংগঠন হয়না। তেমনি কর্মী ছাড়া নেতার উপস্থিতিতেই সংগঠন হয় না। নেতা ও কর্মী বাহিনী মিলিত হলে সম্ভাবনার দিক যেমন ফুটে ওঠে, তেমনি এর সমস্যার দিকও আছে। সেই কারণেই নেতৃত্ব ও কর্মী বাহিনী যাতে নিজেদের দায়িত্ব, কর্তব্য ও অধিকারেরসীমা রেখা সঠিকভাবে জেনে নিতে পারে তার জন্য সংগঠনের কিছু পরিচালনা বিধি থাকে। আধুনিক পরিভাষায় এসব বিধি- বিধানের নাম সংবিধান।', '1', 'http://shibircloud.com/audio/lq/islami_songgothon.mp3', 'https://www.icsbook.info/uploads/bookbook_image/nt6gMFajjB44wrQyKyS7HqZKF5IodzlcYlchvYql.png', 'বাংলাদেশ ইসলামিক সেন্টার', '36.78', '0', 0, 1, NULL, NULL),
(275, 'জামায়াতে ইসলামীর ঊনত্রিশ বছর', '', 53, 0, 'প্রিয় বন্ধুগণ এবং সমবেত ভদ্য মহোদয়গণ! আজ থেকে ঠিক ঊনত্রিশ বছর আগে এ তারিতে এতটি ক্ষুদ্র ঘটনা সংঘটিত হয়। লাহোর নগরীর একটি মহল্লা ইসলামীয়া পার্কে মাওলানা জফর ইকবাল সাহেবের বাড়ীরনিকটে একটি ছোট্ট বাড়ী। সে বাড়ীর একটি কক্ষে এক সম্মেলন অনুষ্টিত হয়। এতে সমগ্র অবিভক্ত ভারত থেকে পঁচাত্তরজন লোক অংশগ্রহণ করেন এবং তারা জামায়াতে ইসলামী নামে একটি দল গঠন করেন।', '1', 'http://shibircloud.com/audio/lq/jamayate_islamir_29_bochor.mp3', 'https://www.icsbook.info/uploads/bookbook_image/29.png', 'জামায়াতে ইসলামী বাংলাদেশ', '24.35', '0', 0, 1, NULL, NULL),
(276, 'ইসলামী দাওয়াত ও কর্মনীতি', '', 53, 0, 'বস্তুত আমরা যে দাওয়াত লইয়া উঠিয়াছি, বর্তমান দুনিয়ার আন্দোলনের বাজারে উহা অপেক্ষা অচল পণ্য বোধ হয় আর একটি ও নাই। উপরন্তু, আমরা যে কর্মনীত গ্রহণ করিয়াছি, তাহাতেও আন্দোলনকে তীব্র গতিতে সম্প্রসারিত করার এবং জনগণকে উহার দিকে আকৃষ্ট করিয়া তুলিবার জন্য বর্তমানকালে সাধারণত যে সব উপায় -উপকরণ গ্রহণ করা হয়, তাহার একটিও বর্তমান নাই। সভার পর সভা করা, মিছিল বাহির করা, রকমারি শ্লোগান দ্বারা আকাশ-বাতাস মুখরিত করা, পতাকা উত্তোলন করা, গরম গরম বক্তৃতা দান প্রভৃতি কোন একটি জিনিসও আমাদের কর্মনীতিতে স্থান পায় নাই। ', '1', 'http://shibircloud.com/audio/lq/islami_daoyat_o_kormoniti.mp3', 'https://www.icsbook.info/uploads/bookbook_image/Rl5dJY4VvN6DEjjk5XBiZUXVlyUya4vd1taSITcX.png', 'জামায়াতে ইসলামী বাংলাদেশ', '19.99', '0', 0, 2, NULL, NULL),
(277, 'বাইয়াতের হাকিকাত', '', 58, 0, 'বাইয়াত ইসলামের একটি গুরুত্বপূর্ণ পরিভাষা।সাহাবায়ে ক্যারাম রাসুল সাঃ এর ইন্তেকালের পর সাহাবায়ে কেরাম আবু বকর রাঃ এর নিকট বাইয়াত হয়েছেন। মুসলমানদের সামস্টিক পরিচালনার দায়িত্ব যার উপর ন্যস্ত হয়েছে তার নিকট বাইয়াত হওয়ার এ তরিকা ইসলামের ইতিহাসে ব্যাপকভাবে স্বীকৃত। প্রথম চার খলিফার পরও বাইয়াতের এ ধারা জারি ছিল। এমনকি যে খানে ইসলামী রাষ্ট্র ব্যাবস্থা নেই সেখানেও দীনি মহলে বাইয়াত শব্দটি বেশ প্রচলিত আছে।', '1', 'http://shibircloud.com/audio/lq/baiyater_hakikat.mp3', 'https://www.icsbook.info/uploads/bookbook_image/KUs0J4URgVcqvc1u6T8jtH0sgTYqgpz2f7XistMS.png', 'জামায়াতে ইসলামী বাংলাদেশ', '10.76', '0', 0, 105, NULL, NULL),
(278, 'বাইয়াতের হাকিকাত', '', 58, 0, 'বাইয়াত ইসলামের একটি গুরুত্বপূর্ণ পরিভাষা।সাহাবায়ে ক্যারাম রাসুল সাঃ এর ইন্তেকালের পর সাহাবায়ে কেরাম আবু বকর রাঃ এর নিকট বাইয়াত হয়েছেন। মুসলমানদের সামস্টিক পরিচালনার দায়িত্ব যার উপর ন্যস্ত হয়েছে তার নিকট বাইয়াত হওয়ার এ তরিকা ইসলামের ইতিহাসে ব্যাপকভাবে স্বীকৃত। প্রথম চার খলিফার পরও বাইয়াতের এ ধারা জারি ছিল। এমনকি যে খানে ইসলামী রাষ্ট্র ব্যাবস্থা নেই সেখানেও দীনি মহলে বাইয়াত শব্দটি বেশ প্রচলিত আছে।', '10', 'https://shibircloud.com/pdf/baiyater_hakikot_v2.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202206/293_1.png', 'জামায়াতে ইসলামী বাংলাদেশ', '1.78', '0', 0, 4, NULL, NULL),
(279, 'জানার জন্য কুরআন মানার জন্য কুরআন', '', 16, 0, 'আল কুরআন আত্মগঠনের শ্রেষ্ঠতম মাধ্যম এবং সাফল্য ও বিজয়। লাভের মাস্টার কী (Master key) । এটা শুধু কথার কথা নয়, বাস্তব ঘটনা 7 সপ্তম শতাব্দীর ইতিহাস খুলে দেখুন। ৬১০ খৃষ্টাব্দে অবতীর্ণ হবার সুচনা থেকে মাত্র কয়েক দশকের মধ্যে আল কুরআন কতো যে অখ্যাত অজ্ঞাত মানুষকে ইতিহাসের স্বর্ণ শিখরে মর্যাদাশীল করেছে। ইতিহাসের অন্তরালে অবস্থিত কত যে কবিলা আর কওমকে বিশ্ব ইতিহাসের সােনালী পত্রে স্থান করে দিয়েছে, তার ইয়ত্তা নেই।', '5', 'http://xeroxtree.com/pdf/janar_jonno_kuran_manar_jonno_koran.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201804/2839_1.jpg', 'শতাব্দী প্রকাশনী', '1.88', '0', 0, 4, NULL, NULL),
(280, 'কুরআন বোঝার পথ ও পাথেয়', '', 16, 0, 'আল কোরআন । আমরা তো এতই বলি যে এটি সর্বকালের সর্বশ্রেষ্ঠ আসমানী কিতাব যা আল্লাহ তাআলার পক্ষ থেকে নাযিল হয়েছে। কিন্তু একটু ও কি এটি নিয়ে ভাবি যে কেন এটা কে বলা হয়েছে একটি জিবন্ত মুজেজা একটি অলৌকিক বই?? আল্লাহ তাআলা কুরআনে একবার নয় দুই বার নয়, তিন তিন বার চ্যালেঞ্জ করেছেন পারলে এমন একটি গ্রন্থ নিয়ে এস পারলে এমন দশটি সুরা নিয়ে এস পারলে এমন একটি সুরা নিয়ে এস।', '5', 'http://xeroxtree.com/pdf/quarn_buzhar_poth_o_patheo.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201704/1588_1.jpg', 'শতাব্দী প্রকাশনী', '3.42', '0', 0, 3, NULL, NULL),
(281, 'দারসুল কুরআন সংকলন - ১ম খন্ড', '', 22, 0, 'রাজা বা সম্রাটকে ঘিরে গড়ে উঠেছিলো একটি অভিজাত শ্রেণী। তাদের আর জনগণের মাঝে ছিলো দুস্তর ব্যবধান। তারা জনসাধারণকে ঘৃণার চোখে দেখতো। কিন্তু তাদের বিলাসী জীবন যাপনের জন্য প্রয়োজনীয় অর্থ ঐ সাধারণ মানুষগুলো থেকেই আদায় করা হতো।', '5', 'http://xeroxtree.com/pdf/darsul_quran_songkolon_nazir_01.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201501/495_1.JPG', 'বাংলাদেশ ইসলামিক সেন্টার', '1.71', '0', 0, 3, NULL, NULL),
(282, 'দারসুল কুরআন সংকলন - ২য় খন্ড', '', 22, 0, 'ইতোপূর্বে দারসুল কুরআন সংকলন-১ প্রকাশিত হয়েছে। কয়েকজন সম্মানিত পাঠক জানিয়েছেন, তাঁরা আমার তৈরি নোটগুলো দ্বারা উপকৃত হয়েছেন। আমি আল্লাহ রাববুল আলামীনের লাখো শুকরিয়া আদায় করছি। তাঁর কাছে আকুতি জানাচ্ছি তিনি যেন আমার এই ক্ষুদ্র প্রয়াস কবুল করে আখিরাতে আমার নাজাতের যারিয়া বানিয়ে দেন।\r\nসম্মানিত পাঠকদের অভিমত আমাকে অনুপ্রাণিত করেছে।', '5', 'http://xeroxtree.com/pdf/darsul_quran_songkolon_nazir_02.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201501/496_1.JPG', 'বাংলাদেশ ইসলামিক সেন্টার', '1.80', '0', 0, 1, NULL, NULL),
(283, 'মহাগ্রন্থ আল- কুরআন কি ও কেন', '', 41, 0, 'বর্বর, বেদুইন, যাযাবর, শিক্ষা ও তাআদ্দুনের আলো হাতে বঞ্চিত একটি জাতি মাত্র অর্ধ শতাব্দীর কম সময়ের ভিতরে পৃথিবীর বৃহত্তম শক্তিদ্বয়কে পর্যুদস্ত ও পরাভূত করে কি করে অর্ধপৃথিবী জয় করে ফেলল? কি করে তাঁরা এই স্বল্পকালীন সময়ের ভিতর মানব সভ্যতার উপরে এক স্থায়ী ও অক্ষয় ছাপ রেখে গেল? সে কথা ভেবে ভেবে আজও ঐতিহাসিকদের পেরেশানীর অন্ত নেই।', '5', 'http://xeroxtree.com/pdf/mohagrontho_al_quran_ki_o_keno.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201410/319_1.jpg', 'খেলাফত পাবলিকেশন্স', '1.94', '0', 0, 0, NULL, NULL),
(284, 'তালীমুল কুরআন', '', 98, 0, 'বাংলাদেশে ৯০% মুসলমান থেকে খুব অল্প সংখ্যক লোকেই বিশুদ্ধ ভাবে কুরআন তিলাওয়াত করতে পাড়ে এবং এর অর্থ বুঝে। সহীহ্ ভাবে কুরআন তিলাওয়াত করা ফর্জে আইন, নামাযের তিলাওয়াতে ভুল হলে নামায হয় না। ', '5', 'http://xeroxtree.com/pdf/talimul_quran.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201410/329_1.jpg', 'ইসলামিক এডুকেশন সোসাইটি', '3.69', '0', 0, 2, NULL, NULL),
(285, 'তাফসীর ফী যিলালিল কোরআন - ১ম খন্ড', '', 99, 0, 'তাফসীরে ফী যিলালিল কোরআন বাংলা তাফসীর গুলোর মধ্যে গ্রহনযোগ্যেতার শীর্ষে থাকা অন্যতম একটি তাফসির। কুরআন থেকে হেদায়েত প্রাপ্ত হলে, হেদায়েতের আলোতে নিজেকে আলোকিত করতে হলে কুরআনকে বুঝতে হবে। আর কুরআন বুঝতে হলে কুরআনের তাফসির অধ্য়য়ন করা আবশ্যক।', '5', 'http://xeroxtree.com/pdf/fi_zilalil_quran_01.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/659_1.jpg', 'আল কোরআন একাডেমী লন্ডন', '7.75', '0', 0, 3, NULL, NULL),
