(305, 'তাফসীর ফী যিলালিল কোরআন - ২০ তম খন্ড', '', 99, 0, 'ফী যিলালিল কুরআন হলো আরবি ভাষার একটি তাফসির গ্রন্থ, লিখেছেন সাইয়্যিদ কুতুব ইব্রাহিম হুসেন সাজলি; যিনি সাইয়েদ কুতুব নামে পরিচিত ( ৯ অক্টোবর ১৯০৬ - ২৯ আগস্ট ১৯৬৬)। তিনি কুরআনের ত্রিশ পাড়া অনুযাযী ত্রিশ খন্ডে তাফসির করেছেন। এটি কয়েকবার পুনর্মুদ্রিত হয়েছে।', '5', 'http://xeroxtree.com/pdf/fi_zilalil_quran_20.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/678_1.jpg', 'আল কোরআন একাডেমী লন্ডন', '11.24', '0', 0, 1, NULL, NULL),
(306, 'কারাগারের রোজনামচা', '', 100, 0, 'কারাগারের রোজনামচা বঙ্গবন্ধু শেখ মুজিবুর রহমান রচিত গ্রন্থ সংকলন। ২০১৭ সালে মুজিবুর রহমানের ৯৮তম জন্মবার্ষিকীতে গ্রন্থটি বাংলা একাডেমি থেকে প্রকাশিত হয়।', '23', 'http://xeroxtree.com/pdf/karagare_rojnamca.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201801/2647_1.jpg', 'বাংলা একাডেমী', '15.08', '0', 0, 7, NULL, NULL);
INSERT INTO `books` (`id`, `name`, `author`, `author_id`, `audio`, `description`, `category_id`, `url`, `image`, `publisher`, `size`, `is_feature`, `is_hide`, `d_count`, `created_at`, `updated_at`) VALUES
(307, 'অসমাপ্ত আত্মজীবনী', '', 100, 0, '২০০৪ সালে শেখ মুজিবুর রহমানের লেখা চারটি খাতা আকস্মিকভাবে তাঁর কন্যা শেখ হাসিনার হস্তগত হয়। খাতাগুলি অতি পুরানো, পাতাগুলি জীর্ণপ্রায় এবং লেখা প্রায়শ অস্পষ্ট। মূল্যবান সেই খাতাগুলি পাঠ করে জানা গেল এটি বঙ্গবন্ধুর অসমাপ্ত আত্মজীবনী, যা তিনি ১৯৬৭ সালের মাঝামাঝি সময়ে ঢাকা সেন্ট্রাল জেলে অন্তরীণ অবস্থায় লেখা শুরু করেছিলেন, কিন্তু শেষ করতে পারেননি। জেল-জুলুম, নিগ্রহ-নিপীড়ন যাঁকে সদা তাড়া করে ফিরেছে, রাজনৈতিক কর্মকাণ্ডে উৎসর্গীকৃত-প্রাণ, সদাব্যস্ত বঙ্গবন্ধু যে আত্মজীবনী লেখায় হাত দিয়েছিলেন এবং কিছুটা লিখেছেনও, এই বইটি তার সাক্ষর বহন করছে।', '23', 'http://xeroxtree.com/pdf/oshomapto_attojiboni.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201506/756_1.JPG', 'দি ইউনিভার্সিটি প্রেস লিমিটেড', '16.19', '0', 0, 8, NULL, NULL),
(308, 'শেখ মুজিব আমার পিতা', '', 101, 0, 'শেখ হাসিনার লেখা \"শেখ মুজিব আমার পিতা\" বইটি একই সঙ্গে তিনটি ভাষায় প্রকাশিত হচ্ছে। ইংরেজি, হিন্দি ও জার্মান এই তিন ভাষায় বইটি প্রকাশের উদ্যোগ নেয়া হয়েছে বলে জানিয়েছেন বইটির প্রকাশক ও আগামী প্রকাশনের স্বত্বাধিকারী ওসমান গণি। ', '23', 'http://xeroxtree.com/pdf/shekh_mujib_amar_pita.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201801/2515_1.jpg', 'আগামী প্রকাশনী', '3.79', '0', 0, 3, NULL, NULL),
(309, 'ময়ূরাক্ষী', '', 89, 0, 'ময়ূরাক্ষী হুমায়ুন আহমেদের লেখা হিমু সিরিজের প্রথম বই। এর প্রকাশকাল ১৯৯০ সাল। এই বইয়ের মাধ্যমেই পাঠকরা খুজে পায় অন্য এক স্বাদ এবং অন্যন্য এক চরিত্র। যার নাম হিমু। এই উপন্যাস এর দ্বারা হিমু চরিত্র হয়ে উঠে তুমুল জনপ্রিয়। এটা এতোটাই জনপ্রিয় হয়েছিলো যে, হুমায়ূন আহমেদ বলে দেয় হিমু হওয়ার নিয়ম। তারপর অনেকে নিজেকে হিমুর আদলে থাকার চেষ্টা করে। এ্যাই ছেলে এ্যাই এই বাক্য দিয়ে শুরু হয় এই উপন্যাস।', '15', 'https://www.fussilatbd.com/Story%20Books/product_images/Humayun%20Ahmed/Himu/Moyurakkhi-By-Humayun-Ahmed.pdf', 'https://humayunahmedbook.com/wp-content/uploads/2021/04/Moyurakkhi-by-humayun-ahmed.jpg', 'অনন্যা পাবলিকেশন', '5.84', '0', 0, 20, NULL, NULL),
(310, 'দরজার ওপাশে', '', 89, 0, 'হিমু বাংলাদেশের কথাসাহিত্যিক হুমায়ুন আহমেদ সৃষ্ট একটি জনপ্রিয় ও কাল্পনিক চরিত্র। নব্বই দশকে হিমুর প্রথম উপন্যাস ময়ূরাক্ষী প্রকাশিত হয়। প্রাথমিক সাফল্যের পর হিমু চরিত্র বিচ্ছিন্নভাবে হুমায়ুন আহমেদের বিভিন্ন উপন্যাসে প্রকাশিত হতে থাকে। হিমু ধারাবাহিকের দ্বিতীয় উপন্যাস হল দরজার ওপাশে।', '15', 'https://www.fussilatbd.com/Story%20Books/product_images/Humayun%20Ahmed/Himu/Dorjar-Opashe-By-Humayun-Ahmed.pdf', 'https://humayunahmedbook.com/wp-content/uploads/2021/04/Dorjar-Opashe-by-humayun-ahmed.jpg', 'জ্ঞানকোষ প্রকাশনী', '10.23', '0', 0, 15, NULL, NULL),
(311, 'হিমু ', '', 89, 0, 'হিমু বাংলাদেশের কথাসাহিত্যিক হুমায়ুন আহমেদ সৃষ্ট একটি জনপ্রিয় ও কাল্পনিক চরিত্র। হিমু মূলত একজন বেকার যুবক, যার আচরণে বেখেয়ালী, জীবনযাপনে ছন্নছাড়া ও বৈষয়িক ব্যাপারে সম্পূর্ণ উদাসীন ভাব প্রকাশ পায়।', '15', 'https://www.fussilatbd.com/Story%20Books/product_images/Humayun%20Ahmed/Himu/Himu-by-Humayun-Ahmed.PDF', 'https://bdebooks.com/wp-content/uploads/2017/06/Himu-by-Humayun-Ahmed.jpg', 'প্রতীক প্রকাশনা সংস্থা', '4.70', '0', 0, 14, NULL, NULL),
(312, 'পারাপার', '', 89, 0, 'হিমুকে ইয়াকুব আলী নামে এক বড়লোক খোজ করেন। যার ব্লাড ক্যানসার ছিল। তাকে কিছুদিন পরপর রক্ত পরিবর্তন করতে হতো। তিনি স্বপ্নে তার মৃত স্ত্রীকে দেখতেন, তার মৃত স্ত্রী তাকে স্বপ্নে বলে,তাকে যদি কোন নিষ্পাপ পবিত্র মানুষের রক্ত দেওয়া যায় তবে তিনি পুরোপুরি সুস্থ হয়ে যাবে। হিমু তার জন্যে পবিত্র মানুষ খুঁজে বেড়ায়।', '15', 'https://www.fussilatbd.com/Story%20Books/product_images/Humayun%20Ahmed/Himu/Parapar-by-Humayun-Ahmed.pdf', 'https://humayunahmedbook.com/wp-content/uploads/2021/06/Parapar-by-humayun-ahmed-pdf-download.jpg', 'অন্যপ্রকাশ', '9.98', '0', 0, 16, NULL, NULL),
(313, 'এবং হিমু', '', 89, 0, 'রাত একটা; হিমু হেঁটে চলেছে রাস্তায়। তার প্রচণ্ড খিদে পেয়েছে কিন্তু সে পড়েছে রূপার দেয়া পকেটবিহীন পাঞ্জাবী। এক সময় সে তার ফুফুর বাড়িতে যায়। সেখানে গিয়ে দেখে মাছের কাটা ফুটায় তার ফুফাত ভাই বাদল না খেয়ে রয়েছে। বাদলের বিশ্বাস একমাত্র হিমুই পারে তার কাটা দূর করে দিতে। এক পর্যায়ে বাদলের কাটা দূর করে হিমু কিন্তু হিমুর কারণেই যে তা হয়েছে অবিশ্বাস করে ইরা। বাদল চায় একদিনের জন্য হলেও যেন ইরা হিমু হয়ে যায়।', '15', 'https://www.fussilatbd.com/Story%20Books/product_images/Humayun%20Ahmed/Himu/Ebong-Himu-By-Humayun-Ahmed.pdf', 'https://humayunahmedbook.com/wp-content/uploads/2021/06/ebong-himu-pdf-download.jpg', 'সময় প্রকাশন', '14.09', '0', 0, 12, NULL, NULL),
(314, 'হিমুর হাতে কয়েকটি নীলপদ্ম', '', 89, 0, 'হুমায়ূন আহমেদের সৃষ্ট চরিত্রগুলোর মধ্যে হিমু অন্যতম। হিমু সিরিজের প্রথম বই হলো ময়ূরাক্ষী (১৯৯০)। হিমু সিরিজের বইগুলোর মধ্যে হিমুর হাতে কয়েকটি নীলপদ্ম ৬ষ্ঠ। হিমুর হাতে কয়েকটি নীলপদ্ম বইটি ১৯৯৬ সালে দিব্যপ্রকাশ থেকে বইটি বের হয়।', '15', 'https://www.fussilatbd.com/Story%20Books/product_images/Humayun%20Ahmed/Himu/Himur-Hate-Koyekti-Neel-Poddo-By-Humayun-Ahmed.pdf', 'https://humayunahmedbook.com/wp-content/uploads/2021/05/Screenshot_2.jpg', 'দিব্যপ্রকাশ', '2.44', '0', 0, 11, NULL, NULL),
(315, 'হিমুর দ্বিতীয় প্রহর', '', 89, 0, 'নন্দিত কথাসাহিত্যিক হুমায়ূন আহমেদের সৃষ্ট চরিত্রগুলোর মধ্যে হিমু অন্যতম। হিমু সিরিজের প্রথম বই হলো ময়ূরাক্ষী (১৯৯০)। প্রাথমিক সাফল্যের পর হিমু চরিত্র বিচ্ছিন্নভাবে হুমায়ুন আহমেদের বিভিন্ন উপন্যাসে প্রকাশিত হতে থাকে। হিমু সিরিজের বইগুলোর মধ্যে হিমুর দ্বিতীয় প্রহর একটু ব্যতিক্রম। এই উপন্যাসে হিমুর সাথে দেখা হয় হুমায়ূন আহমেদের আরও একটি বিখ্যাত চরিত্র মিসির আলির। হিমুর দ্বিতীয় প্রহর উপন্যাসটি হিমু চরিত্রের ৭ম উপন্যাস। বইটি ১৯৯৭ সালের বইমেলায় প্রকাশিত হয়। বইটি প্রকাশনা হলো কাকলী প্রকাশনী। ', '15', 'https://www.fussilatbd.com/Story%20Books/product_images/Humayun%20Ahmed/Himu/Himur-Ditiyo-Prohor-By-Humayun-Ahmed.pdf', 'https://humayunahmedbook.com/wp-content/uploads/2021/06/Himur-ditiyo-prohor-pdf-download.jpg', 'কাকলী প্রকাশনী', '5.67', '0', 0, 11, NULL, NULL),
(316, 'হিমুর রূপালী রাত্রি', '', 89, 0, 'হিমু সিরিজের বইগুলোর মধ্যে হিমুর রূপালী রাত্রি বইটি ৮ম। নন্দিত কথাসাহিত্যিক হুমায়ূন আহমেদ-এর সৃষ্ট চরিত্রগুলোর মধ্যে হিমু অন্যতম। হিমু সিরিজের প্রথম বই হলো ময়ূরাক্ষী (১৯৯০)। প্রাথমিক সাফল্যের পর হিমু চরিত্র বিচ্ছিন্নভাবে হুমায়ুন আহমেদের বিভিন্ন উপন্যাসে প্রকাশিত হতে থাকে। হিমুর রূপালী রাত্রি বইটি ১৯৯৮ সালের অক্টোবর মাসে প্রকাশিত হয়। জ্ঞানকোষ প্রকাশনী থেকে বইটি বের হয়।', '15', 'https://www.fussilatbd.com/Story%20Books/product_images/Humayun%20Ahmed/Himu/Himur-Rupali-Ratri-By-Humayun-Ahmed.pdf', 'https://humayunahmedbook.com/wp-content/uploads/2021/06/Himur-Rupali-Ratri.jpg', ' জ্ঞানকোষ প্রকাশনী', '2.86', '0', 0, 11, NULL, NULL),
(317, 'একজন হিমু কয়েকটি ঝিঁ ঝিঁ পোকা', '', 89, 0, 'হিমু সিরিজের বইগুলোর মধ্যে একজন হিমু কয়েকটি ঝিঁ ঝিঁ পোকা বইটি ৯ম। নন্দিত কথাসাহিত্যিক হুমায়ূন আহমেদ-এর সৃষ্ট চরিত্রগুলোর মধ্যে হিমু অন্যতম। হিমু সিরিজের প্রথম বই হলো ময়ূরাক্ষী (১৯৯০)। প্রাথমিক সাফল্যের পর হিমু চরিত্র বিচ্ছিন্নভাবে হুমায়ুন আহমেদের বিভিন্ন উপন্যাসে প্রকাশিত হতে থাকে। একজন হিমু কয়েকটি ঝিঁ ঝিঁ পোকা বইটি ১৯৯৯ সালের মে মাসে প্রথম প্রকাশিত হয়।', '15', 'https://www.fussilatbd.com/Story%20Books/product_images/Humayun%20Ahmed/Himu/Ekjon-Himu-Koekti-Jhin-Jhin-Poka-By-Humayun-Ahmed.pdf', 'https://humayunahmedbook.com/wp-content/uploads/2021/06/Ekjon-Himu-Koyekti-Jhi-Jhi-Poka.jpg', 'পার্ল পাবলিকেশন্স', '9.69', '0', 0, 10, NULL, NULL),
(318, 'তোমাদের এই নগরে', '', 89, 0, 'হিমু সিরিজের বইগুলোর মধ্যে তোমাদের এই নগরে বইটি ১০ম। নন্দিত কথাসাহিত্যিক হুমায়ূন আহমেদ-এর সৃষ্ট চরিত্রগুলোর মধ্যে হিমু অন্যতম। হিমু সিরিজের প্রথম বই হলো ময়ূরাক্ষী (১৯৯০)। প্রাথমিক সাফল্যের পর হিমু চরিত্র বিচ্ছিন্নভাবে হুমায়ুন আহমেদের বিভিন্ন উপন্যাসে প্রকাশিত হতে থাকে। তোমাদের এই নগরে বইটি ২০০০ সালে প্রথম প্রকাশিত হয়।', '15', 'https://www.fussilatbd.com/Story%20Books/product_images/Humayun%20Ahmed/Himu/Tomader-Ei-Nogore-By-Humayun-Ahmed.pdf', 'https://humayunahmedbook.com/wp-content/uploads/2021/06/Tomader-Ei-Nogore.jpg', 'অনন্যা', '4.90', '0', 0, 15, NULL, NULL),
(319, 'চলে যায় বসন্তের দিন', '', 89, 0, 'হিমু সিরিজের বইগুলোর মধ্যে চলে যায় বসন্তের দিন বইটি ১১তম। নন্দিত কথাসাহিত্যিক হুমায়ূন আহমেদ-এর সৃষ্ট চরিত্রগুলোর মধ্যে হিমু অন্যতম। হিমু সিরিজের প্রথম বই হলো ময়ূরাক্ষী (১৯৯০)। প্রাথমিক সাফল্যের পর হিমু চরিত্র বিচ্ছিন্নভাবে হুমায়ুন আহমেদের বিভিন্ন উপন্যাসে প্রকাশিত হতে থাকে। বইটি ২০০২ সালের বইমেলায় প্রকাশিত হয়।', '15', 'https://www.fussilatbd.com/Story%20Books/product_images/Humayun%20Ahmed/Himu/Chole-Jay-Boshonter-Din-By-Humayun-Ahmed.pdf', 'https://humayunahmedbook.com/wp-content/uploads/2021/06/Chole-jay-bosonter-din.jpg', 'অন্যপ্রকাশ', '11.08', '0', 0, 17, NULL, NULL),
