(321, ' কুরআন ব্যাখ্যার মূলনীতি', '', 84, 0, 'শাহ ওয়ালিউল্লাহ মুহাদ্দিসে দেহলভী (রঃ) এর খ্যাতনামা গ্রন্থ “আল ফাউযুল কবীর ফী উসুলিত তাফসীর নতুন করে কোন পরিচয়ের অপেক্ষা রাখে না। জনাব অধ্যাপক আখতার ফারুক উক্ত কিতাবের অনুবাদ করে নাম দিয়েছেন “কুরআন ব্যাখ্যার মূলনীতি”।', '5', 'http://xeroxtree.com/pdf/quran_bekkhar_mulniti.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/633_1.jpg', 'কুতুব খানায়ে রশিদিয়া', '3.99', '0', 0, 3, NULL, NULL),
(322, 'তাফহীমুল কুরআন ১ম খন্ড ', '', 53, 0, 'কুরআন অধ্যয়নের আগে একজন সাধারন পাঠককে এমন কিছু কথা ভালোভাবে জেনে নিতে হবে যেগুলো শুরুতেই জেনে নিলে তার পক্ষে কুরআনের বক্তব্য অনুধাবন করা সহজ হয়ে যায়। নয়তো কুরআন অধ্যয়নের মাঝখানে বারবার একথাগুলো তার মনে সন্দেহ সঞ্চার করতে পারে। অনেক সময় শুধুমাত্র এগুলো না বুঝার কারণে মানুষ কুরআনের অন্তর্নিহিত অর্থের কেবলমাত্র উপরিভাগে আসতে থাকে বছরের পর বছর ধরে। ভেতরে প্রবেশ করার আর কোন পথই খুঁজে পায় না।', '5', 'https://pathshala.xoventechdev.xyz/appFile/tafheem/tafheem1.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/658_1.jpg', 'আধুনিক প্রকাশনী', '8.30', '0', 0, 7, NULL, NULL),
