(342, 'আর রাহীকুল মাখতূম', '', 102, 0, 'আর রাহীকুল মাখতুম-ছিপি আঁটা মূল্যবান পানীয় দিয়ে আল্লাহ তায়ালা সেদিন তার আরশে আযীমে যার জন্যে এই দস্তরখান সাজাবেন, তিনি হচ্ছেন কুল মাখলুকাতের নয়নমণি হযরত মোহাম্মদ সাল্লাল্লাহু আলাইহি ওয়া সাল্লাম।', '26', 'http://xeroxtree.com/pdf/ar_raheequl_makhtoom.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201501/561_1.jpg', 'আল কোরআন একাডেমী লন্ডন', '17.55', '0', 0, 23, NULL, NULL),
(343, 'সীরাতে ইবনে হিশাম', '', 103, 0, 'জাহিলিয়াত যুগে আরবরা ইতিহাস কি জিনিস, তা জানতো না। ইতিহাস বলতে তাদের কাছে ছিল উত্তরাধিকার সূত্রে প্রাপ্ত জনশ্রুতি। আরব জীবনের স্বভাব +প্রকৃতির বর্ণনাই ছিল সেকালের প্রচলিত ইতিহাসের একমাত্র উপাদান। বাপদাদার বীরত্বগাথা এবং তাদের বদান্যতা, স্বগোত্রের প্রতি আনুগত্য ও প্রতিশ্রুতি পরায়ণতার গৌরবময় কাহিনীতে তা ছিল ভরপুর। বংশ পরম্পরা ও শত্রু মিত্রের পরিচয়মূলক বিবরণে সমৃদ্ধ ছিল সে ইতিহাস। ', '26', 'https://jamaat-e-islami.org/en/publication/file/57_sirat_ibn_hisham.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201502/640_1.jpg', 'বাংলাদেশ ইসলামিক সেন্টার', '13.20', '0', 0, 11, NULL, NULL),
(344, 'আল- কুরআনে রাষ্ট্র ও সরকার', '', 40, 0, 'রাষ্ট্র ও সরকার মানব জীবনের দুটি অপরিহার্য বিষয়। দুনিয়ায় মানুষের স্থিতি, বিকাশ ও সমৃদ্ধির জন্যে রাষ্ট্র ও সরকারের অস্তিত্ব একান্তই আবশ্যক। অন্তত মানুষের সমাজব্দ্ধ জীবন যে রাষ্ট্র ও সরকার ছাড়া চলতে পারে না, এ বিষয়ে প্রাচীন ও আধুনিক কালের মনীষীগন প্রায় সকলেই একমত। তবে কোন্ ধরনের রাষ্ট্র ও সরকার মানুষের জন্যে সত্যিকারভাবে কল্যাণকর, সে বিষয়ে মনীষীদের মধ্যে বিস্তর মতভেদ রয়েছে।', '7', 'http://xeroxtree.com/pdf/al_qurane_rastro_o_sorkar.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201501/559_1.jpg', 'খায়রুন প্রকাশনী', '11.03', '0', 0, 7, NULL, NULL),
(345, 'শরহে বেকায়াহ ১ম খণ্ড', '', 104, 0, 'বেকায়া নামক মূল কিতাবটি তাজুশ শরীয়াহ মাহমুদ ইবনে সদরুশ শরীয়াতিল আকবর তাঁর প্রিয় নাতী ওবায়দুল্লাহ ইবনে মাসউদকে শিক্ষাদানের জন্যে রচনা করেছিলেন। অতঃপর ওবায়দুল্লাহ ইবনে মাসউদ মূল মতনের সাথে আরও ব্যাখ্যা সংযোজন করে এর নাম দেন শরহে বেকায়া।\r\nশিক্ষার্থীদের কল্যাণে আমাদের সংযোজন-\r\n১. ইলমে ফিকহ ও শরহে বেকায়া সম্পর্কে মুকাদ্দিমা সংযোজন।\r\n২. আব্দুল হাই লক্ষ্মৌভী (র) লিখিত মুকাদ্দিমাটি কম্পিউটার কম্পোজ করে সংযোজন।\r\n৩. গ্রন্থকারের জীবনী উল্লেখ।', '11', 'http://xeroxtree.com/pdf/sohoj_shrohe_bekaya_1.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201801/2513_1.jpg', 'আল-কাউসার প্রকাশনী', '18.04', '0', 0, 12, NULL, NULL),
(346, 'শরহে বেকায়া ২য় খণ্ড', '', 104, 0, 'বেকায়া নামক মূল কিতাবটি তাজুশ শরীয়াহ মাহমুদ ইবনে সদরুশ শরীয়াতিল আকবর তাঁর প্রিয় নাতী ওবায়দুল্লাহ ইবনে মাসউদকে শিক্ষাদানের জন্যে রচনা করেছিলেন। অতঃপর ওবায়দুল্লাহ ইবনে মাসউদ মূল মতনের সাথে আরও ব্যাখ্যা সংযোজন করে এর নাম দেন শরহে বেকায়া।\r\nশিক্ষার্থীদের কল্যাণে আমাদের সংযোজন-\r\n১. ইলমে ফিকহ ও শরহে বেকায়া সম্পর্কে মুকাদ্দিমা সংযোজন।\r\n২. আব্দুল হাই লক্ষ্মৌভী (র) লিখিত মুকাদ্দিমাটি কম্পিউটার কম্পোজ করে সংযোজন।\r\n৩. গ্রন্থকারের জীবনী উল্লেখ।', '11', 'http://xeroxtree.com/pdf/sohoj_shrohe_bekaya_2.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201801/2514_1.jpg', 'আল-কাউসার প্রকাশনী', '11.99', '0', 0, 7, NULL, NULL),
(347, 'সাহাবায়ে কিরামের মর্যাদা', '', 53, 0, 'নবী করিম সা:-এর পরে এই উম্মতের শ্রেষ্ঠ প্রজন্ম হলেন সাহাবায়ে কেরামের প্রজন্ম। কুরআন ও সুন্নাহর পর তাদের কথা ও কর্ম উম্মতের জন্য দলিল। কিয়ামত পর্যন্ত আগত সমগ্র মানুষের জন্য তারা সত্যের মাপকাঠি, হেদায়েতের আলোকবর্তিকা। নবুয়াতের দাওয়াত গ্রহণ ও প্রচারের জন্য নবী করিম সা:-এর সাথী হিসেবে আল্লাহ তায়ালা সাহাবায়ে কেরামের জামাতকেই নির্বাচন করেছেন। তাদের প্রতি নিজের রাজি-খুশির কথা স্পষ্ট করে ঘোষণা করেছেন। ', '18', 'http://xeroxtree.com/pdf/sahabaye_keramer_morjada.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201705/1735_1.jpg', 'শতাব্দী প্রকাশনী', '2.23', '0', 0, 24, NULL, NULL),
(348, 'দায়ী ইলাল্লাহ-দাওয়াত ইলাল্লাহ', '', 53, 0, 'আল্লাহ তায়ালা আরবের বিখ্যাত কেন্দ্রীয় শহর মক্কায় তাঁর এ বান্দা (মুহাম্মদ সঃ) –কে নবুওয়াতের দায়িত্ব পালনের জন্য নিযক্তি করেন এবং তাঁর নিজ শহর ও নিজ গোত্র কোরাইশদের মধ্যেই এ দাওয়াতী কাজের সূচনা করতে নির্দেশ দেন। এ কাজের সূচনাতেই যে সব হেদায়েতের প্রজোজন ছিলো তা তাকেঁ প্রদান করা হয় এবং সেগুলো ছিলো প্রধান তিন প্রকারের বিষয়বস্তু সমন্বিতঃ', '18', 'https://shibircloud.com/pdf/dayi_ilallah_daoyat_ilallah.pdf', 'http://www.pathagar.com/contents/records/bookmaster/202206/580_1.png', 'আধুনিক প্রকাশনী', '1.60', '0', 0, 42, NULL, NULL),
(349, 'রুকনিয়াতের আসল চেতনা', '', 58, 0, 'ইসলামী সমাজ প্রতিষ্ঠার এই আন্দোলন আম্বিয়ায়ে কেরামদের পরিচালিত আন্দোলনকে অনুসরণ করেই চলেছে।\r\nমানবিক দুর্বলতার কারণে ব্যক্তির নিষ্ক্রিয়তা থেকে উত্তরণের দিকনির্দেশনা মূলক বই এটি।', '7', 'http://xeroxtree.com/pdf/rukoniyater_ashol_chetona.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201610/1098_1.jpg', 'বাংলাদেশ জামায়াতে ইসলামী', '0.56', '0', 0, 12, NULL, NULL),
(350, 'আখেরাতের প্রস্তুতি', '', 9, 0, 'জীবনের কাজ এবং এর সফলতা ও ব্যর্থতার হিসাব করতে গেলে দুনিয়া ও আখেরাতের বাস্তবতা সম্পর্কে ধারণা থাকা জরুরি। মানুষ দুনিয়ার এ সামান্য সময়ের জীবন-যাপনকে যতবেশি গুরুত্ব দেয় এর চেয়ে তুলনাহীন গুরুত্বপূর্ণ পরকাল। কেননা পরকাল হবে সীমাহীন। যেখানে জীবনের শুরু আছে শেষ বলে কোনো কিছু নেই। এ হাদিসে দুনিয়ার তুলনায় সব বিষয়ে পরকালের বাস্তবতা বা দৃষ্টান্ত পেশ করা হয়েছে। চিন্তাশীল মানুষের জন্য বিষয়টি খুবই গুরুত্বপূর্ণ।', '18', 'http://xeroxtree.com/pdf2/akherater_prostuti.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201907/3459_1.jpg', 'আল ইসলাহ প্রকাশনী', '2.17', '0', 0, 31, NULL, NULL),
(351, 'ইসলামী সমাজ বিনির্মাণে নারীর কাজ', '', 53, 0, 'মহান আল্লাহ রাব্বুল আলামীনের এক অপূর্ব সৃষ্টি হচ্ছে নারী। নারীকে সম্মানিত করেছেন মা হিসেবে, ভগ্নি হিসেবে এবং স্ত্রী হিসেবে। নারী ও পুরুষ উভয়ের মাধ্যমেই মহান আল্লাহ রাব্বুল আলামীন সৃষ্টির ক্রমধারা অব্যাহত রেখেছেন। জাহেলিয়াতের বর্বর যুগে নারীর কোন সূক্ষ্ম অস্তিত্ব ছিল না। কন্যা সন্তানের জীবন্ত কবর দেয়া হতো। কারণ পিতারা তাদের কন্যাদের বাবা হিসেবে পরিচয় দিতে লজ্জাবোধ করতো।', '18', 'https://pathshala.xoventechdev.xyz/appFile/tafheem/narir%20kaj.pdf', 'https://pathshala.xoventechdev.xyz/appFile/tafheem/narir_kaj-min.PNG', 'আহসান পাবলিকেশন্স', '18.95', '0', 0, 155, NULL, NULL),
(352, 'পোশাক পর্দা ও দেহ-সজ্জা', '', 28, 0, 'আধুনিক পাশ্চাত্য সভ্যতা মানুষের জৈবিক বা পাশবিক জীবনকেই একমাত্র উদ্দেশ্য হিসাবে গ্রহণ করেছে। এজন্য পাশ্চাত্য সভ্যতার দৃষ্টিতে ‘স্মার্টনেস’ বা ‘ব্যক্তিত্বে’-র অন্যতম বৈশিষ্ট্য ‘অহঙ্কার’। যাকে দেখলে যত ‘অহঙ্কারী’ বা ‘কঠিন’ মনে হবে সে তত বেশি ‘ব্যক্তিত্বসম্পন্ন’ বা ‘স্মার্ট’। পাশ্চাত্য পোশাক পরিচ্ছদে এই বৈশিষ্ট্য রক্ষার জন্য সদা চেষ্টা করা হয়।', '10', 'http://xeroxtree.com/pdf2/poshak_porda_o_deho_sojja.pdf', 'http://www.pathagar.com/contents/records/bookmaster/201908/3707_1.jpg', 'আস-সুন্নাহ পাবলিকেশন্স', '10.71', '0', 0, 5, NULL, NULL);
